import { z } from "zod";
import { TRPCError } from "@trpc/server";
import { createTRPCRouter, publicProcedure, authedProcedure } from "../trpc";

// Validation schemas
const profileCreateSchema = z.object({
  authId: z.string().uuid(),
});

const profileUpdateSchema = z.object({
  id: z.string().uuid(),
  // Add any other fields you want to allow updating
});

// Finder schema with exclusive fields
const profileFindSchema = z
  .object({
    id: z.string().uuid().optional(),
    authId: z.string().uuid().optional(),
  })
  .refine((data) => data.id ?? data.authId, {
    message: "Either id or authId must be provided",
  });

export const profileRouter = createTRPCRouter({
  // Create a new profile
  create: publicProcedure
    .input(profileCreateSchema)
    .mutation(async ({ ctx, input }) => {
      try {
        return await ctx.db.profile.create({
          data: {
            authId: input.authId,
          },
        });
      } catch (error) {
        if (
          error instanceof Error &&
          error.message.includes("Unique constraint failed")
        )
          throw new TRPCError({
            code: "CONFLICT",
            message: "Profile with this auth ID already exists",
          });

        throw new TRPCError({
          code: "INTERNAL_SERVER_ERROR",
          message: "Failed to create profile",
          cause: error,
        });
      }
    }),

  // General purpose find profile
  findUnique: publicProcedure
    .input(profileFindSchema)
    .query(async ({ ctx, input }) => {
      const where = input.id ? { id: input.id } : { authId: input.authId! };

      const profile = await ctx.db.profile.findUnique({
        where,
        include: {
          studioMembers: {
            include: {
              studio: true,
              permissions: true,
            },
          },
        },
      });

      if (!profile) {
        throw new TRPCError({
          code: "NOT_FOUND",
          message: "Profile not found",
        });
      }

      return profile;
    }),

  // Get current user's profile
  getCurrent: authedProcedure.query(async ({ ctx }) => {
    // The profile is already loaded in the auth middleware,
    // so we can return it directly
    if (!ctx.profile) {
      throw new TRPCError({
        code: "NOT_FOUND",
        message: "Current profile not found",
      });
    }

    // Get a fresh copy with all relations
    const profile = await ctx.db.profile.findUnique({
      where: { id: ctx.profile.id },
      include: {
        studioMembers: {
          include: {
            studio: true,
            permissions: true,
          },
        },
      },
    });

    if (!profile) {
      throw new TRPCError({
        code: "NOT_FOUND",
        message: "Current profile not found",
      });
    }

    return profile;
  }),

  // Update a profile
  update: authedProcedure
    .input(profileUpdateSchema)
    .mutation(async ({ ctx, input }) => {
      // Security check: ensure users can only update their own profile
      if (ctx.profile?.id !== input.id) {
        throw new TRPCError({
          code: "FORBIDDEN",
          message: "You can only update your own profile",
        });
      }

      try {
        return await ctx.db.profile.update({
          where: { id: input.id },
          data: {
            // Add fields you want to allow updating here
            updatedAt: new Date(),
          },
        });
      } catch (error) {
        if (
          error instanceof Error &&
          error.message.includes("Record to update not found")
        ) {
          throw new TRPCError({
            code: "NOT_FOUND",
            message: "Profile not found",
          });
        }

        throw new TRPCError({
          code: "INTERNAL_SERVER_ERROR",
          message: "Failed to update profile",
          cause: error,
        });
      }
    }),

  // Delete a profile with all related data
  delete: authedProcedure
    .input(z.object({ id: z.string().uuid() }))
    .mutation(async ({ ctx, input }) => {
      // Security check: ensure users can only delete their own profile
      if (ctx.profile?.id !== input.id) {
        throw new TRPCError({
          code: "FORBIDDEN",
          message: "You can only delete your own profile",
        });
      }

      // Use a transaction to ensure all related data is deleted properly
      try {
        return await ctx.db.$transaction(async (tx) => {
          // First delete all studio memberships
          await tx.studioMember.deleteMany({
            where: { profileId: input.id },
          });

          // Then delete the profile itself
          return tx.profile.delete({
            where: { id: input.id },
          });
        });
      } catch (error) {
        if (
          error instanceof Error &&
          error.message.includes("Record to delete does not exist")
        ) {
          throw new TRPCError({
            code: "NOT_FOUND",
            message: "Profile not found",
          });
        }

        throw new TRPCError({
          code: "INTERNAL_SERVER_ERROR",
          message: "Failed to delete profile",
          cause: error,
        });
      }
    }),

  // List all profiles (admin only - would need proper authorization)
  list: publicProcedure
    .input(
      z.object({
        page: z.number().min(1).default(1),
        orderBy: z.enum(["createdAt", "updatedAt"]).default("createdAt"),
        order: z.enum(["asc", "desc"]).default("desc"),
        // Can add filters here if needed
      }),
    )
    .query(async ({ ctx, input }) => {
      try {
        const { page, orderBy, order } = input;

        const [profiles, meta] = await ctx.db.profile
          .paginate({
            orderBy: {
              [orderBy]: order,
            },
          })
          .withPages({
            page,
          });

        return {
          profiles,
          meta,
        };
      } catch (error) {
        throw new TRPCError({
          code: "INTERNAL_SERVER_ERROR",
          message: "Failed to list profiles",
          cause: error,
        });
      }
    }),
});
