import { z } from "zod";
import { TRPCError } from "@trpc/server";
import { createTRPCRouter, publicProcedure, authedProcedure } from "../trpc";

// Validation schemas
const studioCreateSchema = z.object({
  name: z.string().min(2).max(100),
  description: z.string().optional(),
  address: z.string().min(5).max(255),
  city: z.string().min(2).max(100),
  state: z.string().min(2).max(100),
  postalCode: z.string().min(3).max(20),
  country: z.string().length(2),
  logo: z.string().url().optional(),
  openingHours: z.record(z.string(), z.array(z.string())).optional(),
});

const studioUpdateSchema = z.object({
  id: z.string().uuid(),
  name: z.string().min(2).max(100).optional(),
  description: z.string().optional().nullable(),
  address: z.string().min(5).max(255).optional(),
  city: z.string().min(2).max(100).optional(),
  state: z.string().min(2).max(100).optional(),
  postalCode: z.string().min(3).max(20).optional(),
  country: z.string().length(2).optional(),
  logo: z.string().url().optional().nullable(),
  openingHours: z.record(z.string(), z.array(z.string())).optional(),
  isActive: z.boolean().optional(),
});

const studioFindSchema = z.object({
  id: z.string().uuid(),
});

const studioQuerySchema = z.object({
  page: z.number().min(1).default(1),
  perPage: z.number().min(1).max(100).default(20),
  orderBy: z.enum(["name", "city", "createdAt", "updatedAt"]).default("createdAt"),
  order: z.enum(["asc", "desc"]).default("desc"),
  search: z.string().optional(),
  city: z.string().optional(),
  country: z.string().optional(),
  isActive: z.boolean().optional(),
});

export const studioRouter = createTRPCRouter({
  // Create a new studio
  create: authedProcedure
    .input(studioCreateSchema)
    .mutation(async ({ ctx, input }) => {
      if (!ctx.profile) {
        throw new TRPCError({
          code: "UNAUTHORIZED",
          message: "You must be logged in to create a studio",
        });
      }

      try {
        // Use a transaction to create the studio and add the creator as owner
        return await ctx.db.$transaction(async (tx) => {
          // Create the studio first
          const studio = await tx.studio.create({
            data: {
              name: input.name,
              description: input.description,
              address: input.address,
              city: input.city,
              state: input.state,
              postalCode: input.postalCode,
              country: input.country,
              logo: input.logo,
              openingHours: input.openingHours as object,
            },
          });

          // Add the creator as an owner
          await tx.studioMember.create({
            data: {
              profileId: ctx.profile.id,
              studioId: studio.id,
              role: "OWNER",
            },
          });

          return studio;
        });
      } catch (error) {
        throw new TRPCError({
          code: "INTERNAL_SERVER_ERROR",
          message: "Failed to create studio",
          cause: error,
        });
      }
    }),

  // Get a studio by ID
  getById: publicProcedure
    .input(studioFindSchema)
    .query(async ({ ctx, input }) => {
      const studio = await ctx.db.studio.findUnique({
        where: { id: input.id },
        include: {
          members: {
            include: {
              profile: true,
              permissions: true,
            },
          },
        },
      });

      if (!studio) {
        throw new TRPCError({
          code: "NOT_FOUND",
          message: "Studio not found",
        });
      }

      return studio;
    }),

  // Update a studio
  update: authedProcedure
    .input(studioUpdateSchema)
    .mutation(async ({ ctx, input }) => {
      if (!ctx.profile) {
        throw new TRPCError({
          code: "UNAUTHORIZED",
          message: "You must be logged in to update a studio",
        });
      }

      // Check if the user has permission to update this studio
      const membership = await ctx.db.studioMember.findFirst({
        where: {
          profileId: ctx.profile.id,
          studioId: input.id,
          role: { in: ["OWNER", "ADMIN"] },
        },
      });

      if (!membership) {
        throw new TRPCError({
          code: "FORBIDDEN",
          message: "You don't have permission to update this studio",
        });
      }

      try {
        // Extract the ID and remove it from the data to be updated
        const { id, ...updateData } = input;

        return await ctx.db.studio.update({
          where: { id },
          data: {
            ...updateData,
            openingHours: updateData.openingHours as object | undefined,
            updatedAt: new Date(),
          },
        });
      } catch (error) {
        if (
          error instanceof Error &&
          error.message.includes("Record to update not found")
        ) {
          throw new TRPCError({
            code: "NOT_FOUND",
            message: "Studio not found",
          });
        }

        throw new TRPCError({
          code: "INTERNAL_SERVER_ERROR",
          message: "Failed to update studio",
          cause: error,
        });
      }
    }),

  // Delete a studio
  delete: authedProcedure
    .input(z.object({ id: z.string().uuid() }))
    .mutation(async ({ ctx, input }) => {
      if (!ctx.profile) {
        throw new TRPCError({
          code: "UNAUTHORIZED",
          message: "You must be logged in to delete a studio",
        });
      }

      // Check if the user is the owner
      const isOwner = await ctx.db.studioMember.findFirst({
        where: {
          profileId: ctx.profile.id,
          studioId: input.id,
          role: "OWNER",
        },
      });

      if (!isOwner) {
        throw new TRPCError({
          code: "FORBIDDEN",
          message: "Only studio owners can delete a studio",
        });
      }

      try {
        // Use a transaction to delete all related data
        return await ctx.db.$transaction(async (tx) => {
          // First delete all studio memberships
          await tx.studioMember.deleteMany({
            where: { studioId: input.id },
          });

          // Then delete the studio itself
          return tx.studio.delete({
            where: { id: input.id },
          });
        });
      } catch (error) {
        if (
          error instanceof Error &&
          error.message.includes("Record to delete does not exist")
        ) {
          throw new TRPCError({
            code: "NOT_FOUND",
            message: "Studio not found",
          });
        }

        throw new TRPCError({
          code: "INTERNAL_SERVER_ERROR",
          message: "Failed to delete studio",
          cause: error,
        });
      }
    }),

  // List all studios with filtering, sorting, and pagination
  list: publicProcedure
    .input(studioQuerySchema)
    .query(async ({ ctx, input }) => {
      try {
        const { 
          page, 
          perPage,
          orderBy, 
          order,
          search,
          city,
          country,
          isActive 
        } = input;

        // Build where conditions based on filters
        const where: any = {};

        if (search) {
          where.OR = [
            { name: { contains: search, mode: 'insensitive' } },
            { description: { contains: search, mode: 'insensitive' } },
            { address: { contains: search, mode: 'insensitive' } },
          ];
        }

        if (city) {
          where.city = { contains: city, mode: 'insensitive' };
        }

        if (country) {
          where.country = country;
        }

        if (isActive !== undefined) {
          where.isActive = isActive;
        }

        // Get total count
        const total = await ctx.db.studio.count({ where });

        // Get paginated results
        const studios = await ctx.db.studio.findMany({
          where,
          orderBy: { [orderBy]: order },
          skip: (page - 1) * perPage,
          take: perPage,
          include: {
            _count: {
              select: { members: true },
            },
          },
        });

        return {
          studios,
          meta: {
            currentPage: page,
            perPage,
            totalPages: Math.ceil(total / perPage),
            totalItems: total,
          },
        };
      } catch (error) {
        throw new TRPCError({
          code: "INTERNAL_SERVER_ERROR",
          message: "Failed to list studios",
          cause: error,
        });
      }
    }),

  // Get studios managed by the current user
  myStudios: authedProcedure
    .input(z.object({
      role: z.enum(["OWNER", "ADMIN", "INSTRUCTOR", "STAFF"]).optional(),
    }).optional())
    .query(async ({ ctx, input }) => {
      if (!ctx.profile) {
        throw new TRPCError({
          code: "UNAUTHORIZED",
          message: "You must be logged in to view your studios",
        });
      }

      try {
        const where: any = {
          profileId: ctx.profile.id,
        };

        if (input?.role) {
          where.role = input.role;
        }

        const memberships = await ctx.db.studioMember.findMany({
          where,
          include: {
            studio: true,
            permissions: true,
          },
          orderBy: {
            createdAt: 'desc',
          },
        });

        return memberships;
      } catch (error) {
        throw new TRPCError({
          code: "INTERNAL_SERVER_ERROR",
          message: "Failed to retrieve your studios",
          cause: error,
        });
      }
    }),

  // Add a member to a studio
  addMember: authedProcedure
    .input(z.object({
      studioId: z.string().uuid(),
      profileId: z.string().uuid(),
      role: z.enum(["ADMIN", "INSTRUCTOR", "STAFF"]).default("STAFF"),
      permissionIds: z.array(z.string().uuid()).optional(),
    }))
    .mutation(async ({ ctx, input }) => {
      if (!ctx.profile) {
        throw new TRPCError({
          code: "UNAUTHORIZED",
          message: "You must be logged in to add members",
        });
      }

      // Check if the current user has permission to add members (owner or admin)
      const hasPermission = await ctx.db.studioMember.findFirst({
        where: {
          profileId: ctx.profile.id,
          studioId: input.studioId,
          role: { in: ["OWNER", "ADMIN"] },
        },
      });

      if (!hasPermission) {
        throw new TRPCError({
          code: "FORBIDDEN",
          message: "You don't have permission to add members to this studio",
        });
      }

      try {
        // Check if the member already exists
        const existingMember = await ctx.db.studioMember.findUnique({
          where: {
            profileId_studioId: {
              profileId: input.profileId,
              studioId: input.studioId,
            },
          },
        });

        if (existingMember) {
          throw new TRPCError({
            code: "CONFLICT",
            message: "This person is already a member of this studio",
          });
        }

        // Create the member
        const member = await ctx.db.studioMember.create({
          data: {
            profileId: input.profileId,
            studioId: input.studioId,
            role: input.role,
            permissions: input.permissionIds 
              ? { connect: input.permissionIds.map(id => ({ id })) }
              : undefined,
          },
          include: {
            profile: true,
            studio: true,
            permissions: true,
          },
        });

        return member;
      } catch (error) {
        throw new TRPCError({
          code: "INTERNAL_SERVER_ERROR",
          message: "Failed to add member to studio",
          cause: error,
        });
      }
    }),

  // Update a member's role or permissions
  updateMember: authedProcedure
    .input(z.object({
      memberId: z.string().uuid(),
      role: z.enum(["ADMIN", "INSTRUCTOR", "STAFF"]).optional(),
      permissionIds: z.array(z.string().uuid()).optional(),
    }))
    .mutation(async ({ ctx, input }) => {
      if (!ctx.profile) {
        throw new TRPCError({
          code: "UNAUTHORIZED",
          message: "You must be logged in to update members",
        });
      }

      // Get the member to update
      const memberToUpdate = await ctx.db.studioMember.findUnique({
        where: { id: input.memberId },
        include: { studio: true },
      });

      if (!memberToUpdate) {
        throw new TRPCError({
          code: "NOT_FOUND",
          message: "Member not found",
        });
      }

      // Check if the current user has permission to update members (owner or admin)
      const hasPermission = await ctx.db.studioMember.findFirst({
        where: {
          profileId: ctx.profile.id,
          studioId: memberToUpdate.studioId,
          role: { in: ["OWNER", "ADMIN"] },
        },
      });

      if (!hasPermission) {
        throw new TRPCError({
          code: "FORBIDDEN",
          message: "You don't have permission to update members in this studio",
        });
      }

      // Check if trying to update someone with a higher role
      if (
        memberToUpdate.role === "OWNER" || 
        (hasPermission.role === "ADMIN" && memberToUpdate.role === "ADMIN")
      ) {
        throw new TRPCError({
          code: "FORBIDDEN",
          message: "You cannot modify members with equal or higher roles",
        });
      }

      try {
        if (input.permissionIds !== undefined) {
