  updateMember: authedProcedure
    .input(
      z.object({
        memberId: z.string().uuid(),
        role: z.enum(["ADMIN", "INSTRUCTOR", "STAFF"]).optional(),
        permissionIds: z.array(z.string().uuid()).optional(),
      }),
    )
    .mutation(async ({ ctx, input }) => {
      if (!ctx.profile) {
        throw new TRPCError({
          code: "UNAUTHORIZED",
          message: "You must be logged in to update members",
        });
      }

      // Get the member to update
      const memberToUpdate = await ctx.db.studioMember.findUnique({
        where: { id: input.memberId },
        include: { studio: true },
      });

      if (!memberToUpdate) {
        throw new TRPCError({
          code: "NOT_FOUND",
          message: "Member not found",
        });
      }

      // Check if the current user has permission to update members (owner or admin)
      const hasPermission = await ctx.db.studioMember.findFirst({
        where: {
          profileId: ctx.profile.id,
          studioId: memberToUpdate.studioId,
          role: { in: ["OWNER", "ADMIN"] },
        },
      });

      if (!hasPermission) {
        throw new TRPCError({
          code: "FORBIDDEN",
          message: "You don't have permission to update members in this studio",
        });
      }

      // Check if trying to update someone with a higher role
      if (
        memberToUpdate.role === "OWNER" ||
        (hasPermission.role === "ADMIN" && memberToUpdate.role === "ADMIN")
      ) {
        throw new TRPCError({
          code: "FORBIDDEN",
          message: "You cannot modify members with equal or higher roles",
        });
      }

      try {
        if (input.permissionIds !== undefined) {
          // First remove all existing permissions
          await ctx.db.studioMember.update({
            where: { id: input.memberId },
            data: {
              permissions: {
                set: [],
              },
            },
          });
        }

        // Update the member
        return await ctx.db.studioMember.update({
          where: { id: input.memberId },
          data: {
            role: input.role,
            permissions: input.permissionIds
              ? { connect: input.permissionIds.map((id) => ({ id })) }
              : undefined,
            updatedAt: new Date(),
          },
          include: {
            profile: true,
            studio: true,
            permissions: true,
          },
        });
      } catch (error) {
        throw new TRPCError({
          code: "INTERNAL_SERVER_ERROR",
          message: "Failed to update studio member",
          cause: error,
        });
      }
    }),

  // Remove a member from a studio
  removeMember: authedProcedure
    .input(
      z.object({
        memberId: z.string().uuid(),
      }),
    )
    .mutation(async ({ ctx, input }) => {
      if (!ctx.profile) {
        throw new TRPCError({
          code: "UNAUTHORIZED",
          message: "You must be logged in to remove members",
        });
      }

      // Get the member to remove
      const memberToRemove = await ctx.db.studioMember.findUnique({
        where: { id: input.memberId },
        include: { studio: true },
      });

      if (!memberToRemove) {
        throw new TRPCError({
          code: "NOT_FOUND",
          message: "Member not found",
        });
      }

      // Check if the current user has permission (owner or admin)
      const hasPermission = await ctx.db.studioMember.findFirst({
        where: {
          profileId: ctx.profile.id,
          studioId: memberToRemove.studioId,
          role: { in: ["OWNER", "ADMIN"] },
        },
      });

      // Also allow users to remove themselves
      const isSelf = memberToRemove.profileId === ctx.profile.id;

      if (!hasPermission && !isSelf) {
        throw new TRPCError({
          code: "FORBIDDEN",
          message:
            "You don't have permission to remove members from this studio",
        });
      }

      // Prevent removing owners unless it's yourself
      if (memberToRemove.role === "OWNER" && !isSelf) {
        throw new TRPCError({
          code: "FORBIDDEN",
          message: "Studio owners cannot be removed by others",
        });
      }

      try {
        return await ctx.db.studioMember.delete({
          where: { id: input.memberId },
        });
      } catch (error) {
        throw new TRPCError({
          code: "INTERNAL_SERVER_ERROR",
          message: "Failed to remove studio member",
          cause: error,
        });
      }
    }),

  // Get all members of a studio
  getMembers: authedProcedure
    .input(
      z.object({
        studioId: z.string().uuid(),
        role: z.enum(["OWNER", "ADMIN", "INSTRUCTOR", "STAFF"]).optional(),
      }),
    )
    .query(async ({ ctx, input }) => {
      if (!ctx.profile) {
        throw new TRPCError({
          code: "UNAUTHORIZED",
          message: "You must be logged in to view studio members",
        });
      }

      // Check if the user is a member of this studio
      const isMember = await ctx.db.studioMember.findFirst({
        where: {
          profileId: ctx.profile.id,
          studioId: input.studioId,
        },
      });

      if (!isMember) {
        throw new TRPCError({
          code: "FORBIDDEN",
          message: "You must be a member of this studio to view its members",
        });
      }

      const whereConditions: Prisma.StudioMemberWhereInput = {
        studioId: input.studioId,
      };

      if (input.role) {
        whereConditions.role = input.role;
      }

      try {
        const members = await ctx.db.studioMember.findMany({
          where: whereConditions,
          include: {
            profile: true,
            permissions: true,
          },
          orderBy: [{ role: "asc" }, { createdAt: "asc" }],
        });

        return members;
      } catch (error) {
        throw new TRPCError({
          code: "INTERNAL_SERVER_ERROR",
          message: "Failed to retrieve studio members",
          cause: error,
        });
      }
    }),
});
