```ts
"use server";

import { revalidatePath } from "next/cache";
import { prisma } from "@/lib/prisma";

export async function createStudio(data: {
  name: string;
  slug: string;
  description?: string;
  address: string;
  city: string;
  state: string;
  postalCode: string;
  country: string;
}) {
  // Check if slug is already taken
  const existingStudio = await prisma.studio.findUnique({
    where: { slug: data.slug },
  });

  if (existingStudio) {
    throw new Error("A studio with this slug already exists.");
  }

  // Create the studio
  const studio = await prisma.studio.create({
    data: {
      name: data.name,
      slug: data.slug,
      description: data.description || null,
      address: data.address,
      city: data.city,
      state: data.state,
      postalCode: data.postalCode,
      country: data.country,
    },
  });

  // Create the studio member relationship with the current user
  // Note: You would need to get the current user's profile ID here
  // This is just a placeholder, replace with actual auth logic
  const userId = '123'; // Replace with actual user ID from auth

  try {
    await prisma.studioMember.create({
      data: {
        profileId: userId,
        studioId: studio.id,
        role: "OWNER",
      },
    });
  } catch (error) {
    // If creating the member relationship fails, delete the studio
    await prisma.studio.delete({
      where: { id: studio.id },
    });
    throw error;
  }

  revalidatePath('/studio');
  return studio;
}
