  SelectValue,
} from "~/components/ui/select";
import {
  Card,
  CardContent,
  CardDescription,
  CardFooter,
  CardHeader,
  CardTitle,
} from "~/components/ui/card";
import { useToast } from "~/components/ui/use-toast";

// Format countries data for select dropdown
const countryOptions = Object.entries(countries).map(([code, country]) => ({
  code,
  name: country.name,
})).sort((a, b) => a.name.localeCompare(b.name));

const studioFormSchema = z.object({
  name: z.string().min(3, "Studio name must be at least 3 characters").max(100),
  slug: z
    .string()
    .min(3, "Slug must be at least 3 characters")
    .max(100)
    .regex(
      /^[a-z0-9-]+$/,
      "Slug can only contain lowercase letters, numbers, and hyphens",
    ),
  description: z.string().optional(),
  address: z.string().min(5, "Address is required"),
  city: z.string().min(2, "City is required"),
  state: z.string().min(2, "State/Province is required"),
  postalCode: z.string().min(2, "Postal code is required"),
  country: z.string().length(2, "Please select a country"),
});

type FormData = z.infer<typeof studioFormSchema>;

export default function NewStudioForm() {
  const router = useRouter();
  const { toast } = useToast();

  const form = useForm<FormData>({
    resolver: zodResolver(studioFormSchema),
    defaultValues: {
      name: "",
      slug: "",
      description: "",
      address: "",
      city: "",
      state: "",
      postalCode: "",
      country: "",
    },
  });

  const createStudio = api.studio.create.useMutation({
    onSuccess: (data) => {
      toast({
        title: "Studio created!",
        description: `${data.name} has been successfully created.`,
      });
      router.push(`/studio/${data.id}`);
    },
    onError: (error) => {
      toast({
        variant: "destructive",
        title: "Error",
        description: error.message || "Failed to create studio. Please try again.",
      });
    },
  });

  function onSubmit(data: FormData) {
    createStudio.mutate(data);
  }

  // Generate slug from name
  function handleNameChange(e: React.ChangeEvent<HTMLInputElement>) {
    const name = e.target.value;
    form.setValue("name", name);

    // Only auto-update slug if it's empty or was auto-generated previously
    const currentSlug = form.getValues("slug");
    const nameValue = form.getValues("name");

    if (!currentSlug || currentSlug === slugify(nameValue.slice(0, -1))) {
      form.setValue("slug", slugify(name));
    }
  }

  return (
    <Card className="shadow-md">
      <CardHeader>
        <CardTitle className="text-2xl sm:text-3xl">
          Create New Studio
        </CardTitle>
        <CardDescription>
          Set up your dance studio profile to start managing classes,
          instructors, and students.
