### 4. Create a custom Form component

```tsx
// components/ui/form.tsx
"use client";

import { createContext, useContext, useEffect, useState } from "react";
import { zodResolver } from "@hookform/resolvers/zod";
import { useForm as useHookForm } from "react-hook-form";
import { z } from "zod";
import { useFormStatus } from "react-dom";
import { cn } from "@/lib/utils";
import { Label } from "@/components/ui/label";
import { toast } from "@/components/ui/use-toast";

const Form = <TSchema extends z.ZodType>({
  schema,
  defaultValues,
  className,
  children,
  action,
  onSuccess,
  onError,
}: {
  schema: TSchema;
  defaultValues?: z.infer<TSchema>;
  className?: string;
  children: (props: {
    form: ReturnType<typeof useHookForm<z.infer<TSchema>>>;
    setValue: (name: any, value: any) => void;
  }) => React.ReactNode;
  action: (data: z.infer<TSchema>) => Promise<any>;
  onSuccess?: (result: any) => void;
  onError?: (error: Error) => void;
}) => {
  const form = useHookForm<z.infer<TSchema>>({
    resolver: zodResolver(schema),
    defaultValues,
    mode: "onBlur",
  });

  const onSubmit = async (data: z.infer<TSchema>) => {
    try {
      const result = await action(data);

      if (result.error) {
        toast({
          variant: "destructive",
          title: "Error",
          description: result.error,
        });
        if (onError) onError(new Error(result.error));
        return;
      }

      if (onSuccess) onSuccess(result);
      form.reset();
    } catch (error) {
      toast({
        variant: "destructive",
        title: "Error",
        description: (error as Error).message || "Something went wrong",
      });
      if (onError) onError(error as Error);
    }
  };

  return (
    <form onSubmit={form.handleSubmit(onSubmit)} className={className}>
      {children({ 
        form, 
        setValue: (name, value) => form.setValue(name, value) 
      })}
    </form>
  );
};

type FormFieldContextValue<
  TFieldValues extends Record<string, any> = Record<string, any>
> = {
  name: string;
};

const FormFieldContext = createContext<FormFieldContextValue>(
  {} as FormFieldContextValue
);

const FormField = <
  TFieldValues extends Record<string, any> = Record<string, any>
>({
  ...props
}: any) => {
  return (
    <FormFieldContext.Provider value={{ name: props.name }}>
      {props.render({
        field: {
          onChange: props.control._fields[props.name]?._f.onChange,
          onBlur: props.control._fields[props.name]?._f.onBlur,
          value: props.control._formValues[props.name],
          name: props.name,
          ref: (instance: any) => {
            if (instance) {
              props.control.register(props.name, {
                required: props.rules?.required,
              })(instance);
            }
          },
        },
        fieldState: {
          invalid: !!props.control.formState.errors[props.name],
          isDirty: !!props.control.formState.dirtyFields[props.name],
          isTouched: !!props.control.formState.touchedFields[props.name],
          error: props.control.formState.errors[props.name],
        },
        formState: props.control.formState,
      })}
    </FormFieldContext.Provider>
  );
};

const FormItem = ({
  className,
  ...props
}: React.HTMLAttributes<HTMLDivElement>) => {
  return (
    <div className={cn("space-y-2", className)} {...props} />
  );
};

const FormLabel = ({
  className,
  ...props
}: React.ComponentPropsWithoutRef<typeof Label>) => {
  return (
    <Label
      className={cn("font-medium", className)}
      {...props}
    />
  );
};

const FormControl = ({
  ...props
}: React.HTMLAttributes<HTMLDivElement>) => {
  return <div {...props} />;
};

const FormDescription = ({
  className,
  ...props
}: React.HTMLAttributes<HTMLParagraphElement>) => {
  return (
    <p
      className={cn("text-[0.8rem] text-muted-foreground", className)}
      {...props}
    />
  );
};

const FormMessage = ({
  className,
  children,
  ...props
}: React.HTMLAttributes<HTMLParagraphElement>) => {
  const { name } = useContext(FormFieldContext);

  return (
    <p
      className={cn("text-[0.8rem] font-medium text-destructive", className)}
      {...props}
    >
      {children}
    </p>
  );
};

const FormSubmit = ({
  children,
  className,
  ...props
}: React.ButtonHTMLAttributes<HTMLButtonElement>) => {
  const { pending } = useFormStatus();

  return (
    <button 
      disabled={pending} 
      className={cn(className)} 
      type="submit" 
      {...props}
    >
      {children}
    </button>
  );
};

export {
  Form,
  FormControl,
  FormDescription,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
  FormSubmit,
};
```

