```ts lokal-link-be/src/features/shops/dto/create-shop.dto.ts
import { z } from "zod";
import { CreateAddressDto } from "@/features/address/dto/create-address.dto.ts";
import { addressTypeEnum } from "@/db/schema/index.ts";
import { DayOfWeek } from "@/lib/constants/day-of-week.ts";

export const CreateShopDto = z.object({
  name: z.string().min(1, "Shop name is required").max(255),
  description: z.string().optional(),
  image: z.string().url("Invalid image URL").optional(),
  slug: z.string().min(1, "Slug is required").max(255),
  ownerProfileId: z.string().uuid("Invalid owner profile ID"),
  addresses: z.array(z.object({
    address: CreateAddressDto,
    street: z.string().min(1, "Street is required").max(255),
    unit: z.string().max(255).optional(),
    type: z.enum(addressTypeEnum.enumValues),
    isMain: z.boolean(),
  })).optional(),
  schedules: z.array(z.object({
    dayOfWeek: z.enum([
      "MONDAY",
      "TUESDAY",
      "WEDNESDAY",
      "THURSDAY",
      "FRIDAY",
      "SATURDAY",
      "SUNDAY",
    ]).transform((day) => DayOfWeek[day]),
    openTime: z.string().regex(/^([0-1]?[0-9]|2[0-3]):[0-5][0-9]$/),
    closeTime: z.string().regex(/^([0-1]?[0-9]|2[0-3]):[0-5][0-9]$/),
    isClosed: z.boolean(),
  })).optional(),
});

export type CreateShopDto = z.infer<typeof CreateShopDto>;
```

```ts lokal-link-be/src/db/schema/products.schema.ts
import {
  boolean,
  decimal,
  index,
  integer,
  jsonb,
  pgTable,
  text,
  uniqueIndex,
  uuid,
  varchar,
} from "drizzle-orm/pg-core";
import { commonColumns } from "@/db/utils.ts";
import { shops } from "@/db/schema/shops.schema.ts";
import { relations } from "drizzle-orm";

// Base Products
export const products = pgTable("products", {
  ...commonColumns,
  shopId: uuid("shop_id").notNull().references(() => shops.id, {
    onDelete: "cascade",
  }),
  name: varchar("name", { length: 255 }).notNull(),
  slug: varchar("slug", { length: 255 }).unique().notNull(),
  description: text("description"),
  sku: varchar("sku", { length: 50 }).notNull().unique(),
  basePrice: decimal("base_price", { precision: 10, scale: 2 }).notNull(),
  isActive: boolean("is_active").notNull().default(true),
  // For products without variants
  inventory: integer("inventory").notNull().default(0),
  lowStockAlert: integer("low_stock_alert").default(10),
  // Metadata for filtering/searching
  metadata: jsonb("metadata"),
}, (table) => [
  // For shop's product listing
  index("products_shop_id_idx").on(table.shopId),
  // For URL lookups
  uniqueIndex("products_slug_idx").on(table.slug),
  // For inventory management
  uniqueIndex("products_sku_idx").on(table.sku),
  // For active products listing by shop
  index("products_shop_active_idx").on(table.shopId, table.isActive),
  // For low stock alerts
  index("products_inventory_alert_idx").on(table.shopId, table.inventory),
]);

export const productsRelations = relations(products, ({ many, one }) => ({
  shop: one(shops, {
    fields: [products.shopId],
    references: [shops.id],
  }),
  variants: many(productVariants),
  categories: many(productToCategories),
  images: many(productImages),
}));

// Product Images
export const productImages = pgTable("product_images", {
  ...commonColumns,
  productId: uuid("product_id")
    .notNull()
    .references(() => products.id, { onDelete: "cascade" }),
  variantId: uuid("variant_id").references(() => productVariants.id),
  url: varchar("url", { length: 2048 }).notNull(),
  alt: varchar("alt", { length: 255 }),
  order: decimal("order", { precision: 10, scale: 2 }).notNull().default("0"),
  isMain: boolean("is_main").notNull().default(false),
}, (table) => [
  // For product images lookup
  index("product_images_product_id_idx").on(table.productId),
  // For variant images lookup
  index("product_images_variant_id_idx").on(table.variantId),
  // For main product image queries
  index("product_images_main_idx").on(table.productId, table.isMain),
  // For ordered image listing
  index("product_images_order_idx").on(table.productId, table.order),
]);

// Product Variants (for products with options like size, color, etc.)
export const productVariants = pgTable("product_variants", {
  ...commonColumns,
  productId: uuid("product_id")
    .notNull()
    .references(() => products.id, { onDelete: "cascade" }),
  sku: varchar("sku", { length: 50 }).notNull().unique(),
  name: varchar("name", { length: 255 }).notNull(),
  // Store variant options (e.g., {"size": "XL", "color": "Red"})
  options: jsonb("options").notNull(),
  price: decimal("price", { precision: 10, scale: 2 }).notNull(),
  inventory: integer("inventory").notNull().default(0),
  isActive: boolean("is_active").notNull().default(true),
}, (table) => [
  // For product variant lookups
  index("product_variants_product_id_idx").on(table.productId),
  // For SKU lookups
  uniqueIndex("product_variants_sku_idx").on(table.sku),
  // For active variants of a product
  index("product_variants_product_active_idx").on(
    table.productId,
    table.isActive,
  ),
  // For inventory management
  index("product_variants_inventory_idx").on(table.productId, table.inventory),
]);

export const productVariantsRelations = relations(
  productVariants,
  ({ one }) => ({
    product: one(products, {
      fields: [productVariants.productId],
      references: [products.id],
    }),
  }),
);

// Product Categories
// Electronics (parentId: null)
// ├── Phones (parentId: electronics_id)
// │   ├── iPhones (parentId: phones_id)
// │   └── Android (parentId: phones_id)
// └── Computers (parentId: electronics_id)
//     ├── Laptops (parentId: computers_id)
//     └── Desktops (parentId: computers_id)
export const productCategories = pgTable("product_categories", {
  ...commonColumns,
  name: varchar("name", { length: 100 }).notNull(),
  slug: varchar("slug", { length: 100 }).notNull().unique(),
  description: text("description"),
  parentId: uuid("parent_id"),
}, (table) => [
  // For URL lookups
  uniqueIndex("product_categories_slug_idx").on(table.slug),
  // For hierarchical queries
  index("product_categories_parent_id_idx").on(table.parentId),
]);

export const productCategoriesRelations = relations(
  productCategories,
  ({ many, one }) => ({
    products: many(productCategories),
    parent: one(productCategories, {
      fields: [productCategories.parentId],
      references: [productCategories.id],
    }),
    children: many(productCategories),
  }),
);

// Junction table for products and categories (many-to-many)
// Why a Junction Table?
// Enables many-to-many relationships
// A product can be in multiple categories
// A category can have multiple products

// An iPhone could be in multiple categories:
// {
//     productId: "iphone-15",
//     categoryId: "smartphones"
//   }
//   {
//     productId: "iphone-15",
//     categoryId: "apple-products"
//   }
export const productToCategories = pgTable("product_to_categories", {
  ...commonColumns,
  productId: uuid("product_id")
    .notNull()
    .references(() => products.id, { onDelete: "cascade" }),
  categoryId: uuid("category_id")
    .notNull()
    .references(() => productCategories.id),
}, (table) => [
  // For product category lookups
  index("product_to_categories_product_id_idx").on(table.productId),
  // For category product lookups
  index("product_to_categories_category_id_idx").on(table.categoryId),
  // Prevent duplicate product-category relationships
  uniqueIndex("product_to_categories_unique_idx").on(
    table.productId,
    table.categoryId,
  ),
]);
```

