// features/address/address.module.ts
export class AddressModule {
  static async create(data: CreateAddressDto) {
    const { shopId, type, street, unit, ...locationData } = data;

    return await db.transaction(async (trx) => {
      // Normalize location data (country, city, zip)
      const normalizedLocation = this.normalizeLocationData(locationData);

      // Find or create base location
      const existingAddress = await AddressService.findExisting(
        normalizedLocation,
        trx
      );

      const addressId = existingAddress 
        ? existingAddress.id
        : (await AddressService.create(normalizedLocation, trx)).id;

      // Create shop-specific address details
      const isMain = await ShopAddressService.shouldBeMainAddress(shopId, trx);
      if (isMain) {
        await ShopAddressService.clearMainAddressFlag(shopId, trx);
      }

      const shopAddress = await ShopAddressService.create({
        shopId,
        addressId,
        street: street.trim(),
        unit: unit?.trim(),
        type,
        isMain
      }, trx);

      // Return complete data
      return {
        ...shopAddress,
        location: normalizedLocation,
      };
    });
  }

  private static normalizeLocationData(location: LocationData): LocationData {
    return {
      country: location.country.toUpperCase().trim(),
      city: location.city.trim(),
      zip: location.zip.replace(/\s+/g, '').toUpperCase(),
    };
  }
}
