```ts lokal-link-be/src/db/schema/shops.schema.ts
import {
  boolean,
  check,
  index,
  integer,
  pgTable,
  smallint,
  text,
  time,
  uniqueIndex,
  uuid,
  varchar,
} from "drizzle-orm/pg-core";
import { relations, sql } from "drizzle-orm";
import { commonColumns } from "../utils.ts";
import { address } from "./address.schema.ts";
import { products } from "./products.schema.ts";
import { profiles } from "./profiles.schema.ts";
import { pgEnum } from "drizzle-orm/pg-core/columns";

export const shops = pgTable("shops", {
  ...commonColumns,
  name: varchar("name", { length: 255 }).notNull(),
  description: text("description"),
  image: varchar("image", { length: 2048 }),
  followersCount: integer("followers_count").notNull().default(0),
  slug: varchar("slug", { length: 255 }).notNull().unique(),
  ownerProfileId: uuid("owner_profile_id")
    .references(() => profiles.id)
    .notNull(),
}, (table) => [
  uniqueIndex("shops_slug_idx").on(table.slug),
  index("shops_owner_profile_id_idx").on(table.ownerProfileId),
]);

export const shopsRelations = relations(shops, ({ many, one }) => ({
  shopAddresses: many(shopAddresses),
  products: many(products),
  schedules: many(shopSchedules),
  followers: many(shopFollowers),
  profile: one(profiles, {
    fields: [shops.ownerProfileId],
    references: [profiles.id],
  }),
}));

export const addressTypeEnum = pgEnum("address_type", [
  "BUSINESS",
  "PHYSICAL",
  "SHIPPING",
  "BILLING",
  "WAREHOUSE",
]);

export const shopAddresses = pgTable("shop_addresses", {
  ...commonColumns,
  shopId: uuid("shop_id")
    .references(() => shops.id)
    .notNull(),
  addressId: uuid("address_id")
    .references(() => address.id)
    .notNull(),
  type: addressTypeEnum("type").notNull(),
  isMain: boolean("is_main").notNull().default(false),
});

export const shopAddressesRelations = relations(shopAddresses, ({ one }) => ({
  shop: one(shops, {
    fields: [shopAddresses.shopId],
    references: [shops.id],
  }),
  address: one(address, {
    fields: [shopAddresses.addressId],
    references: [address.id],
  }),
}));

export const shopSchedules = pgTable("shop_schedules", {
  ...commonColumns,
  shopId: uuid("shop_id")
    .references(() => shops.id)
    .notNull(),
  dayOfWeek: smallint("day_of_week").notNull(),
  openTime: time("open_time").notNull(),
  closeTime: time("close_time").notNull(),
  isClosed: boolean("is_closed").notNull().default(false),
}, (table) => [
  check(
    "day_of_week_range",
    sql`${table.dayOfWeek} >= 1 AND ${table.dayOfWeek} <= 7`,
  ),
  // Index for quick shop schedule lookups
  index("shop_schedules_shop_id_idx").on(table.shopId),
  // Composite index for finding specific day schedules for a shop
  index("shop_schedules_shop_day_idx").on(table.shopId, table.dayOfWeek),
  // Composite index for finding open/closed shops on specific days
  index("shop_schedules_shop_status_idx").on(table.shopId, table.isClosed),
]);

export const shopSchedulesRelations = relations(shopSchedules, ({ one }) => ({
  shop: one(shops, {
    fields: [shopSchedules.shopId],
    references: [shops.id],
  }),
}));

export const shopFollowers = pgTable("shop_followers", {
  ...commonColumns,
  shopId: uuid("shop_id")
    .references(() => shops.id)
    .notNull(),
  customerId: uuid("customer_id")
    .references(() => profiles.id)
    .notNull(),
}, (table) => [
  index("shop_followers_shop_id_idx").on(table.shopId),
  index("shop_followers_customer_id_idx").on(table.customerId),
  uniqueIndex("shop_followers_shop_customer_uniq_idx").on(
    table.shopId,
    table.customerId,
  ),
]);

// Add relation for shop followers
export const shopFollowersRelations = relations(shopFollowers, ({ one }) => ({
  shop: one(shops, {
    fields: [shopFollowers.shopId],
    references: [shops.id],
  }),
  customer: one(profiles, {
    fields: [shopFollowers.customerId],
    references: [profiles.id],
  }),
}));
```

