import { char, index, pgTable, varchar } from "drizzle-orm/pg-core";
import { commonColumns } from "../utils.ts";

export const address = pgTable("address", {
  ...commonColumns,
  country: char("country", { length: 2 }).notNull(),
  city: varchar("city", { length: 100 }).notNull(),
  street: varchar("street", { length: 150 }).notNull(),
  zip: varchar("zip", { length: 20 }).notNull(),
}, (table) => [
  // Composite index for location-based queries (country + city)
  index("address_country_city_idx").on(table.country, table.city),
  // Index for postal code searches
  index("address_zip_idx").on(table.zip),
]);
