Here's the optimized AddressModule implementation with transaction handling and duplicate checking:

```typescript
// features/address/address.module.ts

import { Context } from "hono";
import { eq, and } from "drizzle-orm";
import { AddressService } from "@/features/address/services/address.services.ts";
import { AppResponse } from "@/lib/utils/app-response.ts";
import { Status } from "@/lib/constants/status.ts";
import { CreateAddressDto } from "@/features/address/dto/address.dto.ts";
import { db } from "@/db/index.ts";
import { address, shopAddresses } from "@/db/schema/index.ts";

export class AddressModule {
  static async create(c: Context) {
    const body = await c.req.json();
    const validatedData = CreateAddressDto.safeParse(body);

    if (!validatedData.success) {
      throw AppResponse.error(c).zodError(validatedData.error.errors);
    }

    const { shopId, type, ...addressData } = validatedData.data;

    return await db.transaction(async (trx) => {
      // Check for existing address
      const existingAddress = await trx.query.address.findFirst({
        where: and(
          eq(address.country, addressData.country),
          eq(address.city, addressData.city),
          eq(address.street, addressData.street),
          eq(address.zip, addressData.zip)
        ),
      });

      let addressId;

      if (existingAddress) {
        addressId = existingAddress.id;
      } else {
        // Create new address if doesn't exist
        const newAddress = await trx.insert(address)
          .values(addressData)
          .returning({ id: address.id });
        addressId = newAddress[0].id;
      }

      // Check if this is the first address for the shop
      const existingShopAddresses = await trx.query.shopAddresses.findFirst({
        where: eq(shopAddresses.shopId, shopId),
      });

      // Create shop address association
      const [shopAddress] = await trx.insert(shopAddresses)
        .values({
          shopId,
          addressId,
          type,
          isMain: !existingShopAddresses, // First address becomes main
        })
        .returning();

      return AppResponse.success(
        c,
        { data: shopAddress },
        Status.CREATED
      );
    });
  }
}
```

