import React, { Suspense } from "react";
import Link from "next/link";
import { redirect } from "next/navigation";
import { Calendar, Clock, X } from "lucide-react";
import { NeoCard } from "~/components/ui/neo-card";
import { NeoButton } from "~/components/ui/neo-button";
import { ScheduleForm } from "./schedule-form";
import { api, HydrateClient } from "~/trpc/server";
import { getDanceStyles } from "~/lib/get-dance-style";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "~/components/ui/card";

// Loading skeleton for the page
function AddScheduleSkeleton() {
  return (
    <div className="container mx-auto p-4 md:p-6">
      <div className="neo-border-bottom relative mb-6 pb-6">
        <div className="neo-border mb-4 h-14 w-48 animate-pulse rounded-xl bg-neutral-200 dark:bg-neutral-800"></div>
        <div className="h-5 w-64 animate-pulse rounded-md bg-neutral-200 dark:bg-neutral-800"></div>
      </div>

      <div className="grid gap-6">
        <div className="neo-border rounded-xl bg-white p-6 shadow-neo-sm dark:bg-black">
          {/* Same skeleton content as before */}
        </div>
      </div>
    </div>
  );
}

interface AddSchedulePageProps {
  params: Promise<{
    slug: string;
  }>;
}

// Content component that will be wrapped in Suspense
async function AddScheduleContent({ slug }: { slug: string }) {
  // Get studio ID from slug
  const studio = await api.studio.findUnique({ slug });

  if (!studio) {
    redirect("/profile");
  }

  // Get choreographers for dropdown
  const members = await api.studio.getMembers({
    studioId: studio.id,
    role: "CHOREOGRAPHER",
  });

  return (
    <div className="container mx-auto p-4 md:p-6">
      <Card className="rounded-none border-none shadow-none">
        <CardHeader className="neo-border-bottom relative mb-6 pb-6">
          <div className="flex items-start justify-between">
            <div>
              <div className="neo-border mb-2 inline-block -rotate-1 rounded-xl bg-yellow-400 p-3 shadow-neo-sm">
                <CardTitle className="neo-heading m-0 flex items-center gap-2 text-2xl sm:text-3xl">
                  <Calendar className="h-6 w-6 sm:h-7 sm:w-7" />
                  Add Class Schedule
                </CardTitle>
              </div>
              <CardDescription className="neo-text mt-4 text-base">
                Create a new class for {studio.name}
              </CardDescription>
            </div>

            <Link href={`/profile/${slug}/schedules`}>
              <NeoButton variant="secondary" className="w-full sm:w-auto">
                <X className="mr-2 h-4 w-4" />
                Cancel
              </NeoButton>
            </Link>
          </div>
        </CardHeader>

        <CardContent>
          <NeoCard className="p-6">
            <ScheduleForm
              studioId={studio.id}
              studioSlug={slug}
              choreographers={members}
            />
          </NeoCard>
        </CardContent>
      </Card>
    </div>
  );
}

export default async function AddSchedulePage({
  params,
}: AddSchedulePageProps) {
  const { slug } = await params;
  // Prefetch necessary data
  await api.profile.getCurrent.prefetch();
  await api.schedule.getLevels.prefetch();

  return (
    <HydrateClient>
      <Suspense fallback={<AddScheduleSkeleton />}>
        <AddScheduleContent slug={slug} />
      </Suspense>
    </HydrateClient>
  );
}
