```tsx dance-book-app/src/app/(private)/profile/[slug]/schedules/add/schedule-form.tsx
"use client";

import React, { useState } from "react";
import { useRouter } from "next/navigation";
import { zodResolver } from "@hookform/resolvers/zod";
import { useForm } from "react-hook-form";
import { z } from "zod";
import {
  Calendar,
  Clock,
  DollarSign,
  MapPin,
  Sparkles,
  Users,
  Clipboard,
  CalendarClock,
  Settings,
} from "lucide-react";
import { format } from "date-fns";

import { NeoButton } from "~/components/ui/neo-button";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "~/components/ui/form";
import { Input } from "~/components/ui/input";
import { Textarea } from "~/components/ui/textarea";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "~/components/ui/select";
import { DatePicker } from "~/components/ui/neo-date-picker";
import { Checkbox } from "~/components/ui/checkbox";
import { type Level, type StudioMember } from "@prisma/client";
import { api } from "~/trpc/react";
import { NeoSeparator } from "~/components/ui/neo-separator";
import { SKILL_LEVELS_ARRAY, SkillLevelName } from "~/lib/get-skill-level";
import { DANCE_STYLES_ARRAY, DanceStyleName } from "~/lib/get-dance-style";

// Schema for form validation
const scheduleFormSchema = z.object({
  title: z
    .string()
    .min(2, "Title must be at least 2 characters")
    .max(100, "Title must be at most 100 characters"),
  description: z.string().optional(),
  startDate: z.date({ required_error: "Start date is required" }),
  startTime: z.date({ required_error: "Start time is required" }),
  endTime: z.date({ required_error: "End time is required" }),
  maxCapacity: z.number().int().positive().optional(),
  price: z.number().nonnegative().optional(),
  currency: z.string().length(3).default("USD"),
  location: z.string().max(100).optional(),
  danceStyle: z.union([z.literal("none"), z.nativeEnum(DanceStyleName)]),
  levelId: z.union([z.literal("none"), z.nativeEnum(SkillLevelName)]),
  choreographerId: z.string(), // This can handle "tba" values
});

type ScheduleFormValues = z.infer<typeof scheduleFormSchema>;

interface ScheduleFormProps {
  studioId: string;
  studioSlug: string;
  choreographers: StudioMember[];
}

export function ScheduleForm({
  studioId,
  studioSlug,
  choreographers,
}: ScheduleFormProps) {
  const router = useRouter();
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Setup form with default values
  const form = useForm<ScheduleFormValues>({
    resolver: zodResolver(scheduleFormSchema),
    defaultValues: {
      title: "",
      description: "",
      startDate: new Date(),
      startTime: new Date(),
      endTime: new Date(new Date().setHours(new Date().getHours() + 1)),
      maxCapacity: 20,
      price: 0,
      currency: "USD",
      location: "",
      danceStyle: "none", // Changed from empty string
      levelId: "none", // Keep this as is or use SkillLevelName.ALL_LEVELS if you prefer
      choreographerId: "tba", // Changed from undefined
    },
  });

  // tRPC mutation for creating a schedule
  const createScheduleMutation = api.schedule.create.useMutation({
    onSuccess: () => {
      router.push(`/profile`);
      router.refresh();
    },
  });

  // Process form submission
  async function onSubmit(data: ScheduleFormValues) {
    setIsSubmitting(true);

    try {
      // Combine date and time
      const startTime = new Date(data.startDate);
      const startHours = data.startTime.getHours();
      const startMinutes = data.startTime.getMinutes();
      startTime.setHours(startHours, startMinutes, 0, 0);

      const endTime = new Date(data.startDate);
      const endHours = data.endTime.getHours();
      const endMinutes = data.endTime.getMinutes();
      endTime.setHours(endHours, endMinutes, 0, 0);

      // Validate end time is after start time
      if (endTime <= startTime) {
        form.setError("endTime", {
          type: "manual",
          message: "End time must be after start time",
        });
        setIsSubmitting(false);
        return;
      }

      await createScheduleMutation.mutateAsync({
        studioId,
        title: data.title,
        description: data.description,
        startTime,
        endTime,
        maxCapacity: data.maxCapacity,
        price: data.price,
        currency: data.currency,
        location: data.location,
        danceStyle: data.danceStyle === "none" ? null : data.danceStyle,
        levelId: data.levelId === "none" ? null : data.levelId,
        choreographerId:
          data.choreographerId === "tba" ? null : data.choreographerId,
      });
    } catch (error) {
      console.error("Failed to create schedule:", error);
    } finally {
      setIsSubmitting(false);
    }
  }

  return (
    <Form {...form}>
      <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
        {/* Basic Info Section Header */}
        <div className="mt-8 flex items-center gap-3">
          <div className="neo-border flex h-10 w-10 items-center justify-center rounded-full bg-yellow-200 text-black shadow-neo-sm">
            <Clipboard className="h-5 w-5" strokeWidth={2.5} />
          </div>
          <h3 className="text-lg font-bold">Basic Class Information</h3>
        </div>
        <NeoSeparator className="my-4" />

        <FormField
          control={form.control}
          name="title"
          render={({ field }) => (
            <FormItem>
              <FormLabel className="font-bold">
                Class Title <span className="text-red-500">*</span>
              </FormLabel>
              <FormControl>
                <Input
                  placeholder="e.g., Hip Hop Beginner's Class"
                  {...field}
                  className="bg-white p-3 shadow-neo-sm transition-all focus:translate-x-0.5 focus:translate-y-0.5 focus:shadow-none dark:bg-black"
                />
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        <FormField
          control={form.control}
          name="description"
          render={({ field }) => (
            <FormItem>
              <FormLabel className="font-bold">Description</FormLabel>
              <FormControl>
                <Textarea
                  placeholder="Describe what students will learn in this class..."
                  className="min-h-[120px] resize-y bg-white p-3 shadow-neo-sm transition-all focus:translate-x-0.5 focus:translate-y-0.5 focus:shadow-none dark:bg-black"
                  {...field}
                />
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        <div className="grid gap-6 md:grid-cols-2">
          <FormField
            control={form.control}
            name="danceStyle"
            render={({ field }) => (
              <FormItem>
                <FormLabel className="font-bold">Dance Style</FormLabel>
                <Select
                  onValueChange={field.onChange}
                  defaultValue={field.value}
                >
                  <FormControl>
                    <SelectTrigger className="bg-white shadow-neo-sm transition-all hover:translate-x-0.5 hover:translate-y-0.5 hover:shadow-none dark:bg-black">
                      <SelectValue placeholder="Select a dance style" />
                    </SelectTrigger>
                  </FormControl>
                  <SelectContent className="neo-border shadow-neo-sm">
                    <SelectItem value="none">No specific style</SelectItem>
                    {DANCE_STYLES_ARRAY.map((style) => (
                      <SelectItem key={style.id} value={style.id}>
                        {style.name}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="levelId"
            render={({ field }) => (
              <FormItem>
                <FormLabel className="font-bold">Skill Level</FormLabel>
                <Select
                  onValueChange={field.onChange}
                  defaultValue={field.value}
                >
                  <FormControl>
                    <SelectTrigger className="bg-white shadow-neo-sm transition-all hover:translate-x-0.5 hover:translate-y-0.5 hover:shadow-none dark:bg-black">
                      <SelectValue placeholder="Select skill level" />
                    </SelectTrigger>
                  </FormControl>
                  <SelectContent className="neo-border shadow-neo-sm">
                    <SelectItem value="none">No specific level</SelectItem>
                    {SKILL_LEVELS_ARRAY.map((level) => (
                      <SelectItem key={level.id} value={level.id}>
                        {level.name}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
                <FormMessage />
              </FormItem>
            )}
          />
        </div>

        {/* Schedule Section Header */}
        <div className="mt-8 flex items-center gap-3">
          <div className="neo-border flex h-10 w-10 items-center justify-center rounded-full bg-blue-200 text-black shadow-neo-sm">
            <CalendarClock className="h-5 w-5" strokeWidth={2.5} />
          </div>
          <h3 className="text-lg font-bold">Schedule & Time</h3>
        </div>
        <NeoSeparator className="my-4" />

        <FormField
          control={form.control}
          name="startDate"
          render={({ field }) => (
            <FormItem>
              <FormLabel className="font-bold">
                Date <span className="text-red-500">*</span>
              </FormLabel>
              <FormControl>
                <DatePicker
                  date={field.value}
                  setDate={field.onChange}
                  className="w-full bg-white p-3 shadow-neo-sm transition-all focus:translate-x-0.5 focus:translate-y-0.5 focus:shadow-none dark:bg-black"
                />
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        <div className="grid gap-6 md:grid-cols-2">
          <FormField
            control={form.control}
            name="startTime"
            render={({ field }) => (
              <FormItem>
                <FormLabel className="font-bold">
                  Start Time <span className="text-red-500">*</span>
                </FormLabel>
                <FormControl>
                  <Select
                    value={format(field.value, "HH:mm")}
                    onValueChange={(time) => {
                      const [hours, minutes] = time.split(":").map(Number);
                      const newDate = new Date(field.value);
                      newDate.setHours(hours);
                      newDate.setMinutes(minutes);
                      field.onChange(newDate);
                    }}
                  >
                    <SelectTrigger className="bg-white shadow-neo-sm transition-all hover:translate-x-0.5 hover:translate-y-0.5 hover:shadow-none dark:bg-black">
                      <SelectValue placeholder="Select start time">
                        {format(field.value, "h:mm a")}
                      </SelectValue>
                    </SelectTrigger>
                    <SelectContent className="neo-border max-h-56 overflow-y-auto shadow-neo-sm">
                      {Array.from({ length: 24 * 4 }, (_, i) => {
                        const hour = Math.floor(i / 4);
                        const minute = (i % 4) * 15;
                        const time = `${hour.toString().padStart(2, "0")}:${minute.toString().padStart(2, "0")}`;
                        const date = new Date();
                        date.setHours(hour, minute);
                        return (
                          <SelectItem key={time} value={time}>
                            {format(date, "h:mm a")}
                          </SelectItem>
                        );
                      })}
                    </SelectContent>
                  </Select>
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="endTime"
            render={({ field }) => (
              <FormItem>
                <FormLabel className="font-bold">
                  End Time <span className="text-red-500">*</span>
                </FormLabel>
                <FormControl>
                  <Select
                    value={format(field.value, "HH:mm")}
                    onValueChange={(time) => {
                      const [hours, minutes] = time.split(":").map(Number);
                      const newDate = new Date(field.value);
                      newDate.setHours(hours);
                      newDate.setMinutes(minutes);
                      field.onChange(newDate);
                    }}
                  >
                    <SelectTrigger className="bg-white shadow-neo-sm transition-all hover:translate-x-0.5 hover:translate-y-0.5 hover:shadow-none dark:bg-black">
                      <SelectValue placeholder="Select end time">
                        {format(field.value, "h:mm a")}
                      </SelectValue>
                    </SelectTrigger>
                    <SelectContent className="neo-border max-h-56 overflow-y-auto shadow-neo-sm">
                      {Array.from({ length: 24 * 4 }, (_, i) => {
                        const hour = Math.floor(i / 4);
                        const minute = (i % 4) * 15;
                        const time = `${hour.toString().padStart(2, "0")}:${minute.toString().padStart(2, "0")}`;
                        const date = new Date();
                        date.setHours(hour, minute);
                        return (
                          <SelectItem key={time} value={time}>
                            {format(date, "h:mm a")}
                          </SelectItem>
                        );
                      })}
                    </SelectContent>
                  </Select>
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />
        </div>

        {/* Additional Details Section Header */}
        <div className="mt-8 flex items-center gap-3">
          <div className="neo-border flex h-10 w-10 items-center justify-center rounded-full bg-green-200 text-black shadow-neo-sm">
            <Settings className="h-5 w-5" strokeWidth={2.5} />
          </div>
          <h3 className="text-lg font-bold">Additional Details</h3>
        </div>
        <NeoSeparator className="my-4" />

        <div className="grid gap-6 md:grid-cols-2">
          <FormField
            control={form.control}
            name="location"
            render={({ field }) => (
              <FormItem>
                <FormLabel className="font-bold">Location</FormLabel>
                <FormControl>
                  <Input
                    placeholder="e.g., Studio A, Room 101"
                    {...field}
                    className="bg-white p-3 shadow-neo-sm transition-all focus:translate-x-0.5 focus:translate-y-0.5 focus:shadow-none dark:bg-black"
                  />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="choreographerId"
            render={({ field }) => (
              <FormItem>
                <FormLabel className="font-bold">Instructor</FormLabel>
                <Select
                  onValueChange={field.onChange}
                  defaultValue={field.value}
                >
                  <FormControl>
                    <SelectTrigger className="bg-white shadow-neo-sm transition-all hover:translate-x-0.5 hover:translate-y-0.5 hover:shadow-none dark:bg-black">
                      <SelectValue placeholder="Select an instructor" />
                    </SelectTrigger>
                  </FormControl>
                  <SelectContent className="neo-border shadow-neo-sm">
                    <SelectItem value="tba">To be announced</SelectItem>
                    {choreographers.map((choreographer) => (
                      <SelectItem
                        key={choreographer.id}
                        value={choreographer.id}
                      >
                        {choreographer.profile.name}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
                <FormMessage />
              </FormItem>
            )}
          />
        </div>

        <div className="grid gap-6 md:grid-cols-2">
          <FormField
            control={form.control}
            name="maxCapacity"
            render={({ field }) => (
              <FormItem>
                <FormLabel className="font-bold">Maximum Capacity</FormLabel>
                <FormControl>
                  <Input
                    type="number"
                    min={1}
                    placeholder="20"
                    className="bg-white p-3 shadow-neo-sm transition-all focus:translate-x-0.5 focus:translate-y-0.5 focus:shadow-none dark:bg-black"
                    {...field}
                    onChange={(e) => field.onChange(e.target.valueAsNumber)}
                  />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="price"
            render={({ field }) => (
              <FormItem>
                <FormLabel className="font-bold">Price</FormLabel>
                <div className="flex bg-white dark:bg-black">
                  <FormControl>
                    <Input
                      type="number"
                      min={0}
                      step={0.01}
                      placeholder="0.00"
                      className="flex-1 border-0 bg-transparent p-3"
                      {...field}
                      onChange={(e) => field.onChange(e.target.valueAsNumber)}
                    />
                  </FormControl>
                  <Select
                    value={form.watch("currency")}
                    onValueChange={(value) => form.setValue("currency", value)}
                  >
                    <SelectTrigger className="w-24 border-0 bg-transparent">
                      <SelectValue placeholder="USD" />
                    </SelectTrigger>
                    <SelectContent className="neo-border shadow-neo-sm">
                      <SelectItem value="USD">USD</SelectItem>
                      <SelectItem value="EUR">EUR</SelectItem>
                      <SelectItem value="GBP">GBP</SelectItem>
                      <SelectItem value="CAD">CAD</SelectItem>
                      <SelectItem value="AUD">AUD</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
                <FormMessage />
              </FormItem>
            )}
          />
        </div>

        <div className="pt-6">
          <NeoButton
            type="submit"
            className="w-full transition-all hover:translate-x-0.5 hover:translate-y-0.5 hover:shadow-none"
            disabled={isSubmitting}
          >
            <Sparkles className="mr-2 h-5 w-5" />
            {isSubmitting ? "Creating..." : "Create Class Schedule"}
          </NeoButton>
        </div>
      </form>
    </Form>
  );
}
