```tsx dance-book-app/src/app/(private)/profile/choreographers-form.tsx
"use client";

import { useState, useMemo } from "react";
import { zodResolver } from "@hookform/resolvers/zod";
import { useForm } from "react-hook-form";
import { toast } from "sonner";
import { z } from "zod";
import { Loader2, Mail, Plus, Sparkles } from "lucide-react";
import { api } from "~/trpc/react";

import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTrigger,
} from "~/components/ui/dialog";
import {
  Drawer,
  DrawerContent,
  DrawerTrigger,
  DrawerHeader,
  DrawerTitle,
} from "~/components/ui/drawer";
import {
  Form,
  FormControl,
  FormDescription,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "~/components/ui/form";
import { Input } from "~/components/ui/input";
import { NeoButton } from "~/components/ui/neo-button";
import { Card } from "~/components/ui/card";
import { useIsMobile } from "~/hooks/use-mobile";
import { NeoCard } from "~/components/ui/neo-card";
import { DialogTitle } from "@radix-ui/react-dialog";

// Simplified schema - only email, no role needed
const choreographerInviteSchema = z.object({
  email: z.string().email("Please enter a valid email address"),
});

type ChoreographerInviteValues = z.infer<typeof choreographerInviteSchema>;

const defaultValues: ChoreographerInviteValues = {
  email: "",
};

interface ChoreographerInviteDialogProps {
  studioId: string;
  studioSlug: string;
  buttonLabel?: string;
  buttonClassName?: string;
  variant?: "default" | "outline" | "secondary" | "ghost";
  size?: "default" | "sm" | "lg" | "icon";
}

export function ChoreographerInviteDialog({
  studioId,
  studioSlug,
  buttonLabel = "Add Choreographer",
  buttonClassName = "",
  variant = "default",
  size = "default",
}: ChoreographerInviteDialogProps) {
  const [open, setOpen] = useState(false);
  const [formSubmitted, setFormSubmitted] = useState(false);
  const isMobile = useIsMobile();
  const isDesktop = !isMobile;

  const form = useForm<ChoreographerInviteValues>({
    resolver: zodResolver(choreographerInviteSchema),
    defaultValues,
    mode: "onChange",
  });

  // Use the inviteChoreographer mutation
  const { mutate: inviteChoreographer, isLoading } =
    api.studio.inviteChoreographer.useMutation({
      onSuccess: (data) => {
        setFormSubmitted(true);

        if (data.status === "invited") {
          toast.success(`Invitation sent to ${data.email}!`);
        } else if (data.status === "added") {
          toast.success(`${data.email} was added to your studio!`);
        } else if (data.status === "already_member") {
          toast.info(`${data.email} is already a member of your studio.`);
        } else if (data.status === "role_updated") {
          toast.success(`${data.email}'s role was updated.`);
        } else if (data.status === "already_invited") {
          toast.info(`${data.email} has already been invited.`);
        }
      },
      onError: (error) => {
        toast.error(`Failed to send invitation: ${error.message}`);
      },
    });

  const onSubmit = async (values: ChoreographerInviteValues) => {
    inviteChoreographer({
      studioId,
      email: values.email,
      role: "CHOREOGRAPHER", // Hardcoded role
    });
  };

  const handleNewInvite = () => {
    setFormSubmitted(false);
    form.reset(defaultValues);
  };

  const handleClose = () => {
    setOpen(false);
    // Reset form after a short delay to avoid visual glitches
    setTimeout(() => {
      setFormSubmitted(false);
      form.reset(defaultValues);
    }, 200);
  };

  // Memoize the TriggerButton to prevent unnecessary re-renders
  const TriggerButton = useMemo(
