  FormLabel,
  FormMessage,
} from "~/components/ui/form";

import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "~/components/ui/card";
import { Tabs } from "~/components/ui/tabs";
import { useRef, useState, useMemo, useEffect } from "react";
import { FileUploader } from "~/components/file-upload";
import { OpeningHoursEditor } from "~/components/opening-hours";
import { useSupabaseUpload } from "~/hooks/use-upload-file";
import Image from "next/image";
import { Progress } from "~/components/ui/progress";
import { LocationSelector } from "~/components/ui/location-selector";
import { Building2 } from "lucide-react";
import { NeoCard } from "~/components/ui/neo-card";

const studioFormSchema = z.object({
  name: z.string().min(3, "Studio name must be at least 3 characters").max(100),
  slug: z
    .string()
    .min(3, "Slug must be at least 3 characters")
    .max(100)
    .regex(
      /^[a-z0-9-]+$/,
      "Slug can only contain lowercase letters, numbers, and hyphens",
    ),
  description: z.string().optional(),
  address: z.string().min(5, "Address is required"),
  city: z.string().min(2, "City is required"),
  state: z.string().min(2, "State/Province is required"),
  postalCode: z.string().min(2, "Postal code is required"),
  country: z.string().length(2, "Please select a country"),
  logo: z.string().url().optional(),
  openingHours: z.record(z.string(), z.array(z.string())).optional(),
});

type FormData = z.infer<typeof studioFormSchema>;

export default function NewStudioForm() {
  const router = useRouter();
  const slugManuallyEditedRef = useRef(false);
  const [countryOpen, setCountryOpen] = useState(false);
  const [stateOpen, setStateOpen] = useState(false);
  const [cityOpen, setCityOpen] = useState(false);
  const [activeTab, setActiveTab] = useState("basic");
  const [logoFile, setLogoFile] = useState<File[]>([]);
  const { uploadFile, isUploading, progress } = useSupabaseUpload();

  // States to store location data - properly typed as arrays
  const [states, setStates] = useState<IState[]>([]);
  const [cities, setCities] = useState<ICity[]>([]);

  const form = useForm<FormData>({
    resolver: zodResolver(studioFormSchema),
    defaultValues: {
      name: "",
      slug: "",
      description: "",
      address: "",
      city: "",
      state: "",
      postalCode: "",
      country: "PH", // Philippines as default country
      openingHours: {},
    },
  });

  // Get all countries with proper typing
  const countryList = useMemo(() => {
    // Explicitly cast the return value to ICountry[]
    const countries = Country.getAllCountries();
    return countries.sort((a, b) => a.name.localeCompare(b.name));
  }, []);

  // Watch for country changes to update states
  const selectedCountry = form.watch("country");
  useEffect(() => {
    if (selectedCountry) {
      // Explicitly cast the return value to IState[]
      const countryStates = State.getStatesOfCountry(selectedCountry);
      setStates(countryStates);
      // Reset state and city when country changes
      if (form.getValues("state")) {
        form.setValue("state", "");
        form.setValue("city", "");
        setCities([]);
      }
    }
  }, [selectedCountry, form]);

  // Watch for state changes to update cities
  const selectedState = form.watch("state");
  useEffect(() => {
    if (selectedCountry && selectedState) {
      // Explicitly cast the return value to ICity[]
      const stateCities = City.getCitiesOfState(selectedCountry, selectedState);
      setCities(stateCities);
      // Reset city when state changes
      if (form.getValues("city")) {
        form.setValue("city", "");
      }
    }
  }, [selectedState, selectedCountry, form]);

  const createStudio = api.studio.create.useMutation({
    onSuccess: (data) => {
      if (!data) return;
      router.push(`/studio/${data.slug}`);
    },
  });

  async function onSubmit(data: FormData) {
    try {
      // If there's a logo file, upload it first
      if (logoFile.length > 0 && logoFile[0]) {
        // Pass the slug to organize files
        const logoUrl = await uploadFile(logoFile[0], {
          studioSlug: data.slug,
          assetType: "logos",
        });
        data.logo = logoUrl;
      }

      // Submit the complete data
      createStudio.mutate(data);
    } catch (error) {
      console.error("Error creating studio:", error);
    }
  }

  // Optimized name change handler
  const handleNameChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const name = e.target.value;
    form.setValue("name", name);

    // Only auto-update slug if it hasn't been manually edited
    if (!slugManuallyEditedRef.current) {
      form.setValue("slug", slugify(name), { shouldValidate: false });
    }
  };

  // Handler for slug field to track manual edits
  const handleSlugChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    slugManuallyEditedRef.current = true;
    form.setValue("slug", e.target.value);
  };

  // Reset handler that also resets the manual edit tracking
  const handleReset = () => {
    form.reset({
      name: "",
      slug: "",
      description: "",
      address: "",
      city: "",
      state: "",
      postalCode: "",
      country: "PH", // Ensure Philippines is still selected after reset
      openingHours: {},
    });
    setLogoFile([]);
    slugManuallyEditedRef.current = false;
  };

  // Handle opening hours changes
  const handleOpeningHoursChange = (hours: Record<string, string[]>) => {
    form.setValue("openingHours", hours);
  };

  return (
    <Card className="rounded-none border-none shadow-none">
      <CardHeader className="neo-border-bottom relative mb-6 pb-6">
        <div className="neo-border mb-2 inline-block -rotate-1 rounded-xl bg-yellow-400 p-3 shadow-neo-sm">
          <CardTitle className="neo-heading m-0 flex items-center gap-2 text-2xl sm:text-3xl">
            <Building2 className="h-6 w-6 sm:h-7 sm:w-7" />
            Setup Studio
          </CardTitle>
        </div>
        <CardDescription className="neo-text mt-4 text-base">
          Start managing classes, instructors, and students.
        </CardDescription>
      </CardHeader>

      <CardContent>
        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-8">
            <div className="mb-8 w-full">
              <div className="flex flex-wrap justify-between">
                {["Basic Info", "Address", "Opening Hours"].map(
                  (step, index) => (
                    <div
                      key={index}
                      className="mb-4 flex w-1/3 flex-col items-center px-1 sm:px-2"
                    >
                      <div
                        className={`neo-border flex h-10 w-10 cursor-pointer items-center justify-center rounded-full sm:h-12 sm:w-12 ${
                          activeTab === ["basic", "address", "hours"][index]
                            ? "bg-primary text-primary-foreground shadow-neo-sm dark:shadow-neo-white-sm"
                            : "bg-white dark:bg-black"
                        }`}
                        onClick={() =>
                          setActiveTab(["basic", "address", "hours"][index]!)
                        }
                      >
                        <span className="text-base font-black sm:text-lg">
                          {index + 1}
                        </span>
                      </div>
                      <span className="mt-2 text-center text-xs font-bold sm:text-sm">
                        {step}
                      </span>
                    </div>
                  ),
                )}
              </div>

              <div className="neo-border mt-4 rounded-full bg-white p-1.5 shadow-neo-sm dark:bg-black dark:shadow-neo-white-sm">
                <Progress
                  value={
                    activeTab === "basic"
                      ? 0
                      : activeTab === "address"
                        ? 50
                        : 100
                  }
                  className="h-2 bg-transparent"
                  // indicatorclassname="bg-primary neo-border-sm rounded-full"
                />
              </div>
            </div>

            <Tabs
              defaultValue="basic"
              value={activeTab}
              onValueChange={setActiveTab}
              className="w-full"
            >
              <NeoCard variant="form-section" className="mt-6">
                <div className="space-y-6">
                  {activeTab === "basic" && (
                    <>
                      <div className="grid grid-cols-1 gap-6 sm:grid-cols-2">
                        <div className="flex flex-col space-y-2">
                          <FormLabel className="text-sm font-bold">
                            <RequiredLabel>Studio Logo</RequiredLabel>
                          </FormLabel>

                          {form.watch("logo") ? (
                            <div className="neo-border relative mx-auto aspect-square h-36 w-36 overflow-hidden rounded-lg bg-white dark:bg-black sm:mx-0 sm:h-48 sm:w-48">
                              <Image
                                src={form.watch("logo")!}
                                alt="Studio logo"
                                fill
                                className="object-cover"
                              />
                              <Button
                                type="button"
                                variant="destructive"
                                size="sm"
                                className="neo-border absolute right-2 top-2 bg-red-500 font-bold text-white shadow-neo-sm transition-all hover:translate-x-0.5 hover:translate-y-0.5 hover:bg-red-600 hover:shadow-none"
                                onClick={() => {
                                  form.setValue("logo", undefined);
                                  setLogoFile([]);
                                }}
                              >
                                Remove
                              </Button>
                            </div>
                          ) : (
                            <div className="neo-border mx-auto max-w-[300px] rounded-lg bg-white p-3 shadow-neo-sm dark:bg-black sm:mx-0">
                              <FileUploader
                                value={logoFile}
                                onValueChange={setLogoFile}
                                accept={{
                                  "image/*": [".jpg", ".jpeg", ".png", ".webp"],
                                }}
                                maxSize={1024 * 1024 * 2} // 2MB
                                maxFileCount={1}
                                progresses={progress}
                              />
                            </div>
                          )}
                          <FormDescription className="text-center text-xs font-medium sm:text-left">
                            Upload a square logo image (JPG, PNG). Max 2MB.
                          </FormDescription>
                        </div>

                        <div className="space-y-4">
                          <FormField
                            control={form.control}
                            name="name"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel className="font-bold">
                                  <RequiredLabel>Studio Name</RequiredLabel>
                                </FormLabel>
                                <FormControl>
                                  <Input
                                    placeholder="Rhythm Dance Academy"
                                    onChange={handleNameChange}
                                    value={field.value}
                                    onBlur={field.onBlur}
                                    name={field.name}
                                    ref={field.ref}
                                    className="neo-border bg-white p-3 shadow-neo-sm transition-all focus:translate-x-0.5 focus:translate-y-0.5 focus:shadow-none dark:bg-black"
                                  />
                                </FormControl>
                                <FormMessage />
                              </FormItem>
                            )}
                          />

                          <FormField
                            control={form.control}
                            name="slug"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel className="font-bold">
                                  <RequiredLabel>URL Slug</RequiredLabel>
                                </FormLabel>
                                <FormControl>
                                  <Input
                                    placeholder="rhythm-dance-academy"
                                    onChange={handleSlugChange}
                                    value={field.value}
                                    onBlur={field.onBlur}
                                    name={field.name}
                                    ref={field.ref}
                                    className="neo-border bg-white p-3 shadow-neo-sm transition-all focus:translate-x-0.5 focus:translate-y-0.5 focus:shadow-none dark:bg-black"
                                  />
                                </FormControl>
                                <FormDescription className="font-medium">
                                  Used in your studio URL: dancebook.com/studio/
                                  <span className="font-bold">
                                    {field.value || "your-slug"}
                                  </span>
                                </FormDescription>
                                <FormMessage />
                              </FormItem>
                            )}
                          />
                        </div>
                      </div>

                      <FormField
                        control={form.control}
                        name="description"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel className="font-bold">
                              Description
                            </FormLabel>
                            <FormControl>
                              <Textarea
                                placeholder="Tell us about your studio..."
                                className="neo-border min-h-[100px] resize-y bg-white p-3 shadow-neo-sm transition-all focus:translate-x-0.5 focus:translate-y-0.5 focus:shadow-none dark:bg-black"
                                {...field}
                                value={field.value ?? ""}
                              />
                            </FormControl>
                            <FormDescription className="font-medium">
                              This will appear on your public profile.
                            </FormDescription>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <div className="flex justify-end">
                        <Button
                          type="button"
                          className="neo-border w-full bg-yellow-400 px-4 py-2 font-bold text-black shadow-neo-sm transition-all hover:translate-x-0.5 hover:translate-y-0.5 hover:bg-yellow-300 hover:shadow-none sm:w-auto sm:px-6 sm:py-2.5"
                          onClick={() => setActiveTab("address")}
                        >
                          Next: Address 
                        </Button>
                      </div>
                    </>
                  )}

                  {activeTab === "address" && (
                    <>
                      <FormField
                        control={form.control}
                        name="country"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel className="font-bold">
                              <RequiredLabel>Country</RequiredLabel>
                            </FormLabel>
                            <FormControl>
                              <LocationSelector
                                open={countryOpen}
                                onOpenChange={setCountryOpen}
                                value={field.value}
                                placeholder="Select country"
                                title="Country"
                                items={countryList.map((country) => ({
                                  id: country.isoCode,
                                  name: country.name,
                                  flag: country.flag,
                                }))}
                                onSelect={(value) =>
                                  form.setValue("country", value)
                                }
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={form.control}
                        name="state"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel className="font-bold">
                              <RequiredLabel>State/Province</RequiredLabel>
                            </FormLabel>
                            {states && states.length > 0 ? (
                              <FormControl>
                                <LocationSelector
                                  open={stateOpen}
                                  onOpenChange={setStateOpen}
                                  value={field.value}
                                  placeholder="Select state/province"
                                  title="State"
                                  items={states.map((state) => ({
                                    id: state.isoCode,
                                    name: state.name,
                                  }))}
                                  onSelect={(value) =>
                                    form.setValue("state", value)
                                  }
                                />
                              </FormControl>
                            ) : (
                              <FormControl>
                                <Input
                                  placeholder="Enter state/province"
                                  {...field}
                                  className="neo-border bg-white p-3 shadow-neo-sm transition-all focus:translate-x-0.5 focus:translate-y-0.5 focus:shadow-none dark:bg-black"
                                />
                              </FormControl>
                            )}
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={form.control}
                        name="city"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel className="font-bold">
                              <RequiredLabel>City</RequiredLabel>
                            </FormLabel>
                            {cities && cities.length > 0 ? (
                              <FormControl>
                                <LocationSelector
                                  open={cityOpen}
                                  onOpenChange={setCityOpen}
                                  value={field.value}
                                  placeholder="Select city"
                                  title="City"
                                  items={cities.map((city) => ({
                                    id: city.name,
                                    name: city.name,
                                  }))}
                                  onSelect={(value) =>
                                    form.setValue("city", value)
                                  }
                                />
                              </FormControl>
                            ) : (
                              <FormControl>
                                <Input
                                  placeholder="Enter city"
                                  {...field}
                                  className="neo-border bg-white p-3 shadow-neo-sm transition-all focus:translate-x-0.5 focus:translate-y-0.5 focus:shadow-none dark:bg-black"
                                />
                              </FormControl>
                            )}
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={form.control}
                        name="postalCode"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel className="font-bold">
                              <RequiredLabel>Postal Code</RequiredLabel>
                            </FormLabel>
                            <FormControl>
                              <Input
                                placeholder="1000"
                                {...field}
                                className="neo-border bg-white p-3 shadow-neo-sm transition-all focus:translate-x-0.5 focus:translate-y-0.5 focus:shadow-none dark:bg-black"
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={form.control}
                        name="address"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel className="font-bold">
                              <RequiredLabel>Address</RequiredLabel>
                            </FormLabel>
                            <FormControl>
                              <Input
                                placeholder="123 Dance Street"
                                {...field}
                                className="neo-border bg-white p-3 shadow-neo-sm transition-all focus:translate-x-0.5 focus:translate-y-0.5 focus:shadow-none dark:bg-black"
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <div className="flex flex-col justify-between gap-3 sm:flex-row sm:gap-0">
                        <Button
                          type="button"
                          className="neo-border bg-white px-4 py-2 font-bold text-black shadow-neo-sm transition-all hover:translate-x-0.5 hover:translate-y-0.5 hover:bg-gray-100 hover:shadow-none dark:bg-black dark:text-white dark:shadow-neo-white-sm dark:hover:bg-gray-800 sm:px-6 sm:py-2.5"
                          onClick={() => setActiveTab("basic")}
                        >
                           Back
                        </Button>
                        <Button
                          type="button"
                          className="neo-border bg-yellow-400 px-4 py-2 font-bold text-black shadow-neo-sm transition-all hover:translate-x-0.5 hover:translate-y-0.5 hover:bg-yellow-300 hover:shadow-none sm:px-6 sm:py-2.5"
                          onClick={() => setActiveTab("hours")}
                        >
                          Next: Hours 
                        </Button>
                      </div>
                    </>
                  )}

                  {activeTab === "hours" && (
                    <>
                      <FormField
                        control={form.control}
                        name="openingHours"
                        render={({ field }) => (
                          <FormItem>
                            <FormControl>
                              <OpeningHoursEditor
                                value={field.value}
                                onChange={handleOpeningHoursChange}
                              />
                            </FormControl>
                            <FormDescription className="mt-2 text-center text-sm font-medium">
                              Set your regular business hours. You can add
                              special hours for classes later.
                            </FormDescription>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <div className="flex flex-col justify-between gap-3 sm:flex-row">
                        <Button
                          type="button"
                          className="neo-border order-1 bg-white px-4 py-2 font-bold text-black shadow-neo-sm transition-all hover:translate-x-0.5 hover:translate-y-0.5 hover:bg-gray-100 hover:shadow-none dark:bg-black dark:text-white dark:shadow-neo-white-sm dark:hover:bg-gray-800 sm:order-none sm:px-6 sm:py-2.5"
                          onClick={() => setActiveTab("address")}
                        >
                           Back
                        </Button>
                        <div className="flex flex-col gap-3 sm:flex-row">
                          <Button
                            type="button"
                            onClick={handleReset}
                            disabled={createStudio.isPending || isUploading}
                            className="neo-border bg-white px-4 py-2 font-bold text-black shadow-neo-sm transition-all hover:translate-x-0.5 hover:translate-y-0.5 hover:bg-gray-100 hover:shadow-none dark:bg-black dark:text-white dark:shadow-neo-white-sm dark:hover:bg-gray-800 sm:px-6 sm:py-2.5"
                          >
                            Reset All
                          </Button>
                          <Button
                            type="submit"
                            disabled={createStudio.isPending || isUploading}
                            className="neo-border min-w-0 bg-yellow-400 px-4 py-2 font-bold text-black shadow-neo-sm transition-all hover:translate-x-0.5 hover:translate-y-0.5 hover:bg-yellow-300 hover:shadow-none sm:min-w-32 sm:px-6 sm:py-2.5"
                          >
                            {createStudio.isPending || isUploading
                              ? "Creating..."
                              : "Create Studio"}
                          </Button>
                        </div>
                      </div>
                    </>
                  )}
                </div>
              </NeoCard>
            </Tabs>
          </form>
        </Form>
      </CardContent>
    </Card>
  );
}

// Helper to generate required field labels
const RequiredLabel = ({ children }: { children: React.ReactNode }) => (
  <span className="flex items-center gap-1">
    {children}
    <span className="text-destructive">*</span>
  </span>
);
