"use client";

import { useEffect, useState } from "react";
import { useSearchParams } from "next/navigation";
import { createClient } from "~/lib/supabase/client";
import { useAuthWithInvites } from "~/hooks/useAuthWithInvites";

export default function InviteAcceptPage() {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [email, setEmail] = useState<string | null>(null);
  const [studioName, setStudioName] = useState<string | null>(null);
  const searchParams = useSearchParams();
  const studioId = searchParams.get("studioId");
  const role = searchParams.get("role");
  const supabase = createClient();

  // Use the auth hook to handle invitation processing
  useAuthWithInvites();

  useEffect(() => {
    async function loadInvitationData() {
      if (!studioId || !role) {
        setError("Invalid invitation link");
        setLoading(false);
        return;
      }

      try {
        // Check if user is already authenticated
        const {
          data: { session },
        } = await supabase.auth.getSession();

        if (session?.user) {
          // User is already logged in, update their metadata with invitation data
          await supabase.auth.updateUser({
            data: {
              studioId,
              invitedRole: role,
            },
          });

          // The auth hook will handle the rest
          return;
        }

        // Get the email from the auth link
        const { data: inviteData } = await supabase.auth.getMagicLink();

        if (inviteData?.email) {
          setEmail(inviteData.email);
        }

        // Get studio info to display
        const response = await fetch(`/api/studios/${studioId}/name`);
        const data = await response.json();
        setStudioName(data.name);

        setLoading(false);
      } catch (error) {
        console.error("Error loading invitation data:", error);
        setError("Failed to load invitation data");
        setLoading(false);
      }
    }

    loadInvitationData();
  }, [studioId, role, supabase]);

  if (loading) {
    return <div className="p-8 text-center">Loading invitation...</div>;
  }

  if (error) {
    return <div className="p-8 text-center text-red-500">{error}</div>;
  }

  return (
    <div className="mx-auto my-10 max-w-md rounded-lg bg-white p-6 shadow-lg">
      <h1 className="mb-6 text-2xl font-bold">Join {studioName}</h1>

      {email ? (
        <div className="mb-4">
          <p>
            You've been invited to join {studioName} as a {role?.toLowerCase()}.
          </p>
          <p className="mt-2">
            Please log in or create an account with {email} to accept the
            invitation.
          </p>
        </div>
      ) : (
        <div className="mb-4">
          <p>
            You've been invited to join {studioName}. Please log in or create an
            account to continue.
          </p>
        </div>
      )}

      {/* Login/Signup Form would go here */}
      {/* This depends on your authentication UI components */}
    </div>
  );
}
