import { eq } from "drizzle-orm";
import { shops } from "@/db/schema/index.ts";
import { db } from "@/db/index.ts";
import { ShopColumns, ShopInsert } from "@/features/shops/shops.types.ts";
import * as DrizzleTypes from "@/types/drizzle.type.ts";
type QueryOptions = {
  includeAddresses?: boolean;
  includeSchedules?: boolean;
  columns?: ShopColumns[] | "*";
};

type FindFirstWhere = {
  id?: string;
  ownerProfileId?: string;
  slug?: string;
};

export class ShopsService {
  constructor(private trx: DrizzleTypes.Transaction = db) {}

  static withTransaction(trx: DrizzleTypes.Transaction) {
    return new ShopsService(trx);
  }

  async create(input: ShopInsert) {
    const [shop] = await this.trx
      .insert(shops)
      .values(input)
      .returning();

    console.log("Shop created:", shop);

    return shop;
  }

  static async create(input: ShopInsert) {
    return await new ShopsService().create(input);
  }

  async findFirst(
    where: FindFirstWhere,
    options: QueryOptions = {},
  ) {
    // First try to use unique indexes (faster lookups)
    if (where.id) {
      return await this.findByPrimaryKey(where.id, options);
    }

    if (where.slug) {
      return await this.findByUniqueIndex(where.slug, options);
    }

    // Then fall back to regular indexes
    if (where.ownerProfileId) {
      return await this.findByOwnerProfileId(where.ownerProfileId, options);
    }

    throw new Error("At least one where condition is required");
  }

  static async findFirst(
    where: FindFirstWhere,
    options: QueryOptions = {},
  ) {
    return await new ShopsService().findFirst(where, options);
  }

  private async findByPrimaryKey(
    id: string,
    options: QueryOptions,
  ) {
    return await this.trx.query.shops.findFirst({
      where: eq(shops.id, id),
      ...this.buildQueryOptions(options),
    });
  }

  private async findByUniqueIndex(
    slug: string,
    options: QueryOptions,
  ) {
    return await this.trx.query.shops.findFirst({
      where: eq(shops.slug, slug),
      ...this.buildQueryOptions(options),
    });
  }

  private async findByOwnerProfileId(
    ownerProfileId: string,
    options: QueryOptions,
  ) {
    return await this.trx.query.shops.findFirst({
      where: eq(shops.ownerProfileId, ownerProfileId),
      ...this.buildQueryOptions(options),
    });
  }

  private buildQueryOptions(options: QueryOptions) {
    const {
      includeAddresses = false,
      includeSchedules = false,
      columns = "*",
    } = options;

    return {
      columns: columns === "*"
        ? undefined
        : Object.fromEntries(columns.map((col) => [col, true])),
      with: {
        ...(includeAddresses
          ? { shopAddresses: { with: { address: true } } as const }
          : {}),
        ...(includeSchedules ? { schedules: true as const } : {}),
      },
    };
  }
}
