// lokal-link-be/src/lib/decorators.ts
/**
 * Creates a static version of a class method that instantiates the class and calls the instance method
 */
export function WithStatic() {
  return function (
    target: any,
    propertyKey: string,
    descriptor: PropertyDescriptor,
  ) {
    const originalMethod = descriptor.value;

    if (typeof originalMethod !== "function") {
      throw new Error(`@WithStatic can only be applied to methods`);
    }

    // Ensure we're not overriding an existing static method
    if (target.constructor[propertyKey]) {
      console.warn(
        `Static method ${propertyKey} already exists. Skipping automatic creation.`,
      );
      return descriptor;
    }

    // Create the static version of the method
    target.constructor[propertyKey] = async function (...args: any[]) {
      const instance = new target.constructor();
      return await instance[propertyKey](...args);
    };

    return descriptor;
  };
}

/**
 * Class decorator that adds a static withTransaction method to service classes
 */
export function WithTransaction() {
  return function <T extends new (...args: any[]) => any>(constructor: T): T & {
    withTransaction: (trx: any) => InstanceType<T>;
  } {
    // Use type assertion to add the static method
    const extendedConstructor = constructor as T & {
      withTransaction: (trx: any) => InstanceType<T>;
    };

    // Don't override if it already exists
    if ('withTransaction' in extendedConstructor) {
      console.warn(
        `Static method withTransaction already exists on ${constructor.name}. Skipping automatic creation.`,
      );
      return extendedConstructor;
    }

    // Add the static withTransaction method
    extendedConstructor.withTransaction = function(trx: any) {
      return new constructor(trx);
    };

    return extendedConstructor;
  };
}
