"use client";

import { useRouter } from "next/navigation";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { countries } from "countries-list";
import { api } from "~/trpc/react";
import { slugify } from "~/lib/slugify";
import { Button } from "~/components/ui/button";
import { Input } from "~/components/ui/input";
import { Textarea } from "~/components/ui/textarea";
import {
  Form,
  FormControl,
  FormDescription,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "~/components/ui/form";
import {
  Command,
  CommandEmpty,
  CommandGroup,
  CommandInput,
  CommandItem,
  CommandList,
} from "~/components/ui/command";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "~/components/ui/popover";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "~/components/ui/card";
import { useRef, useState, useMemo } from "react";
import { Check, ChevronsUpDown } from "lucide-react";

// Format countries data for select dropdown with proper typing
type CountryOption = {
  code: string;
  name: string;
  flag: string;
};

const studioFormSchema = z.object({
  name: z.string().min(3, "Studio name must be at least 3 characters").max(100),
  slug: z
    .string()
    .min(3, "Slug must be at least 3 characters")
    .max(100)
    .regex(
      /^[a-z0-9-]+$/,
      "Slug can only contain lowercase letters, numbers, and hyphens",
    ),
  description: z.string().optional(),
  address: z.string().min(5, "Address is required"),
  city: z.string().min(2, "City is required"),
  state: z.string().min(2, "State/Province is required"),
  postalCode: z.string().min(2, "Postal code is required"),
  country: z.string().length(2, "Please select a country"),
});

type FormData = z.infer<typeof studioFormSchema>;

// Create a manually typed country flag mapping function
function getCountryEmoji(countryCode: string): string {
  const codePoints = countryCode
    .toUpperCase()
    .split("")
    .map((char) => 127397 + char.charCodeAt(0));
  return String.fromCodePoint(...codePoints);
}

export default function NewStudioForm() {
  const router = useRouter();
  const slugManuallyEditedRef = useRef(false);
  const [open, setOpen] = useState(false);

  // Cast the countries object to a properly typed record for TypeScript
  const typedCountries = countries as Record<string, { name: string }>;

  // Memoize the country options to improve performance
  const countryOptions = useMemo(() => {
    return Object.entries(typedCountries)
      .map(
        ([code, country]): CountryOption => ({
          code,
          name: country.name,
          flag: getCountryEmoji(code),
        }),
      )
      .sort((a, b) => a.name.localeCompare(b.name));
  }, [typedCountries]);

  const form = useForm<FormData>({
    resolver: zodResolver(studioFormSchema),
    defaultValues: {
      name: "",
      slug: "",
      description: "",
      address: "",
      city: "",
      state: "",
      postalCode: "",
      country: "PH", // Philippines as default country
    },
  });

  const createStudio = api.studio.create.useMutation({
    onSuccess: (data) => {
      router.push(`/studio/${data.slug}`);
    },
  });

  function onSubmit(data: FormData) {
    createStudio.mutate(data);
  }

  // Optimized name change handler
  const handleNameChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const name = e.target.value;
    form.setValue("name", name);

    // Only auto-update slug if it hasn't been manually edited
    if (!slugManuallyEditedRef.current) {
      form.setValue("slug", slugify(name), { shouldValidate: false });
    }
  };

  // Handler for slug field to track manual edits
  const handleSlugChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    slugManuallyEditedRef.current = true;
    form.setValue("slug", e.target.value);
  };

  // Reset handler that also resets the manual edit tracking
  const handleReset = () => {
    form.reset({
      name: "",
      slug: "",
      description: "",
      address: "",
      city: "",
      state: "",
      postalCode: "",
      country: "PH", // Ensure Philippines is still selected after reset
    });
    slugManuallyEditedRef.current = false;
  };

  return (
    <Card className="shadow-md">
      <CardHeader>
        <CardTitle className="text-2xl sm:text-3xl">
          Create New Studio
        </CardTitle>
        <CardDescription>
          Set up your dance studio profile to start managing classes,
          instructors, and students.
        </CardDescription>
      </CardHeader>

      <CardContent>
        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-8">
            <div className="grid gap-6 sm:grid-cols-2">
              <FormField
                control={form.control}
                name="name"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>
                      <RequiredLabel>Studio Name</RequiredLabel>
                    </FormLabel>
                    <FormControl>
                      <Input
                        placeholder="Rhythm Dance Academy"
                        onChange={handleNameChange}
                        value={field.value}
                        onBlur={field.onBlur}
                        name={field.name}
                        ref={field.ref}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="slug"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>
                      <RequiredLabel>URL Slug</RequiredLabel>
                    </FormLabel>
                    <FormControl>
                      <Input
                        placeholder="rhythm-dance-academy"
                        onChange={handleSlugChange}
                        value={field.value}
                        onBlur={field.onBlur}
                        name={field.name}
                        ref={field.ref}
                      />
                    </FormControl>
                    <FormDescription>
                      Used in your studio URL: dancebook.com/studio/
                      {field.value || "your-slug"}
                    </FormDescription>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="description"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Description</FormLabel>
                  <FormControl>
                    <Textarea
                      placeholder="Tell us about your studio..."
                      className="min-h-[100px] resize-y"
                      {...field}
                      value={field.value ?? ""}
                    />
                  </FormControl>
                  <FormDescription>
                    This will appear on your public profile.
                  </FormDescription>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="address"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>
                    <RequiredLabel>Address</RequiredLabel>
                  </FormLabel>
                  <FormControl>
                    <Input placeholder="123 Dance Street" {...field} />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="grid gap-6 sm:grid-cols-2">
              <FormField
                control={form.control}
                name="city"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>
                      <RequiredLabel>City</RequiredLabel>
                    </FormLabel>
                    <FormControl>
                      <Input placeholder="Manila" {...field} />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="state"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>
                      <RequiredLabel>State/Province</RequiredLabel>
                    </FormLabel>
                    <FormControl>
                      <Input placeholder="Metro Manila" {...field} />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <div className="grid gap-6 sm:grid-cols-2">
              <FormField
                control={form.control}
                name="postalCode"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>
                      <RequiredLabel>Postal Code</RequiredLabel>
                    </FormLabel>
                    <FormControl>
                      <Input placeholder="1000" {...field} />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="country"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>
                      <RequiredLabel>Country</RequiredLabel>
                    </FormLabel>
                    <Popover open={open} onOpenChange={setOpen}>
                      <PopoverTrigger asChild>
                        <FormControl>
                          <Button
                            variant="outline"
                            role="combobox"
                            aria-expanded={open}
                            className="w-full justify-between"
                          >
                            {field.value ? (
                              <>
                                <span className="mr-2 text-lg">
                                  {
                                    countryOptions.find(
                                      (country) => country.code === field.value,
                                    )?.flag
                                  }
                                </span>
                                {
                                  countryOptions.find(
                                    (country) => country.code === field.value,
                                  )?.name
                                }
                              </>
                            ) : (
                              "Select country"
                            )}
                            <ChevronsUpDown className="ml-2 h-4 w-4 shrink-0 opacity-50" />
                          </Button>
                        </FormControl>
                      </PopoverTrigger>
                      <PopoverContent className="w-full p-0">
                        <Command>
                          <CommandInput placeholder="Search country..." />
                          <CommandList>
                            <CommandEmpty>No country found.</CommandEmpty>
                            <CommandGroup className="max-h-[300px] overflow-y-auto">
                              {countryOptions.map((country) => (
                                <CommandItem
                                  key={country.code}
                                  value={`${country.name} ${country.code}`}
                                  onSelect={() => {
                                    form.setValue("country", country.code);
                                    setOpen(false);
                                  }}
                                >
                                  <span className="mr-2 text-lg">
                                    {country.flag}
                                  </span>
                                  <span>{country.name}</span>
                                  {country.code === field.value && (
                                    <Check className="ml-auto h-4 w-4" />
                                  )}
                                </CommandItem>
                              ))}
                            </CommandGroup>
                          </CommandList>
                        </Command>
                      </PopoverContent>
                    </Popover>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <div className="flex justify-end gap-3 pt-4">
              <Button
                variant="outline"
                type="button"
                onClick={handleReset}
                disabled={createStudio.isPending}
              >
                Reset
              </Button>
              <Button type="submit" disabled={createStudio.isPending}>
                {createStudio.isPending ? "Creating..." : "Create Studio"}
              </Button>
            </div>
          </form>
        </Form>
      </CardContent>
    </Card>
  );
}

// Helper to generate required field labels
const RequiredLabel = ({ children }: { children: React.ReactNode }) => (
  <span className="flex items-center gap-1">
    {children}
    <span className="text-destructive">*</span>
  </span>
);
