```ts dance-book-app/src/server/api/routers/studio.ts
import { z } from "zod";
import { TRPCError } from "@trpc/server";
import {
  createTRPCRouter,
  publicProcedure,
  authedProcedure,
  adminProcedure,
  ownerProcedure,
  staffProcedure,
} from "../trpc";
import { type Prisma } from "@prisma/client";

// Validation schemas
const studioCreateSchema = z.object({
  name: z.string().min(2).max(100),
  description: z.string().optional(),
  address: z.string().min(5).max(255),
  city: z.string().min(2).max(100),
  state: z.string().min(2).max(100),
  postalCode: z.string().min(3).max(20),
  country: z.string().length(2),
  logo: z.string().url().optional(),
  openingHours: z.record(z.string(), z.array(z.string())).optional(),
});

const studioUpdateSchema = z.object({
  studioId: z.string().uuid(), // Changed from 'id' to 'studioId' for consistency with middleware
  name: z.string().min(2).max(100).optional(),
  description: z.string().optional().nullable(),
  address: z.string().min(5).max(255).optional(),
  city: z.string().min(2).max(100).optional(),
  state: z.string().min(2).max(100).optional(),
  postalCode: z.string().min(3).max(20).optional(),
  country: z.string().length(2).optional(),
  logo: z.string().url().optional().nullable(),
  openingHours: z.record(z.string(), z.array(z.string())).optional(),
  isActive: z.boolean().optional(),
});

// Finder schema with exclusive fields, similar to profile router
const studioFindSchema = z.object({
  id: z.string().uuid(),
});

const studioQuerySchema = z.object({
  page: z.number().min(1).default(1),
  orderBy: z
    .enum(["name", "city", "createdAt", "updatedAt"])
    .default("createdAt"),
  order: z.enum(["asc", "desc"]).default("desc"),
  search: z.string().optional(),
  city: z.string().optional(),
  country: z.string().optional(),
  isActive: z.boolean().optional(),
});

export const studioRouter = createTRPCRouter({
  // Create a new studio
  create: authedProcedure
    .input(studioCreateSchema)
    .mutation(async ({ ctx, input }) => {
      try {
        // Use a transaction to create the studio and add the creator as owner
        return await ctx.db.$transaction(async (tx) => {
          // Create the studio first
          const studio = await tx.studio.create({
            data: {
              name: input.name,
              description: input.description,
              address: input.address,
              city: input.city,
              state: input.state,
              postalCode: input.postalCode,
              country: input.country,
              logo: input.logo,
              openingHours: input.openingHours as object,
            },
          });

          // Add the creator as an owner
          await tx.studioMember.create({
            data: {
              profileId: ctx.profile.id,
              studioId: studio.id,
              role: "OWNER",
            },
          });

          return studio;
        });
      } catch (error) {
        throw new TRPCError({
          code: "INTERNAL_SERVER_ERROR",
          message: "Failed to create studio",
          cause: error,
        });
      }
    }),

  // Find a unique studio, similar to profile router
  findUnique: publicProcedure
    .input(studioFindSchema)
    .query(async ({ ctx, input }) => {
      const studio = await ctx.db.studio.findUnique({
        where: { id: input.id },
        include: {
          members: {
            include: {
              profile: true,
            },
          },
        },
      });

      if (!studio) {
        throw new TRPCError({
          code: "NOT_FOUND",
          message: "Studio not found",
        });
      }

      return studio;
    }),

  // Update a studio - using adminProcedure which already checks for OWNER or ADMIN role
  update: adminProcedure
    .input(studioUpdateSchema)
    .mutation(async ({ ctx, input }) => {
      try {
        // Extract the studioId and remove it from the data to be updated
        const { studioId, ...updateData } = input;

        return await ctx.db.studio.update({
          where: { id: studioId },
          data: {
            ...updateData,
            openingHours: updateData.openingHours as object | undefined,
            updatedAt: new Date(),
          },
        });
      } catch (error) {
        if (
          error instanceof Error &&
          error.message.includes("Record to update not found")
        ) {
          throw new TRPCError({
            code: "NOT_FOUND",
            message: "Studio not found",
          });
        }

        throw new TRPCError({
          code: "INTERNAL_SERVER_ERROR",
          message: "Failed to update studio",
          cause: error,
        });
      }
    }),

  // Delete a studio - using ownerProcedure which already checks for OWNER role
  delete: ownerProcedure
    .input(z.object({ studioId: z.string().uuid() }))
    .mutation(async ({ ctx, input }) => {
      try {
        // Use a transaction to delete all related data
        return await ctx.db.$transaction(async (tx) => {
          // First delete all studio memberships
          await tx.studioMember.deleteMany({
            where: { studioId: input.studioId },
          });

          // Then delete the studio itself
          return tx.studio.delete({
            where: { id: input.studioId },
          });
        });
      } catch (error) {
        if (
          error instanceof Error &&
          error.message.includes("Record to delete does not exist")
        ) {
          throw new TRPCError({
            code: "NOT_FOUND",
            message: "Studio not found",
          });
        }

        throw new TRPCError({
          code: "INTERNAL_SERVER_ERROR",
          message: "Failed to delete studio",
          cause: error,
        });
      }
    }),

  // List all studios with filtering, sorting, and pagination
  list: publicProcedure
    .input(studioQuerySchema)
    .query(async ({ ctx, input }) => {
      try {
        const { page, orderBy, order, search, city, country, isActive } = input;

        // Build query options for pagination
        const paginationOptions: Prisma.StudioFindManyArgs = {
          orderBy: {
            [orderBy]: order,
          },
        };

        // Add filtering conditions
        const whereConditions: Prisma.StudioWhereInput = {};

        if (search) {
          whereConditions.OR = [
            {
              name: {
                contains: search,
                mode: "insensitive" as Prisma.QueryMode,
              },
            },
            {
              description: {
                contains: search,
                mode: "insensitive" as Prisma.QueryMode,
              },
            },
            {
              address: {
                contains: search,
                mode: "insensitive" as Prisma.QueryMode,
              },
            },
          ];
        }

        if (city) {
          whereConditions.city = {
            contains: city,
            mode: "insensitive" as Prisma.QueryMode,
          };
        }

        if (country) {
          whereConditions.country = country;
        }

        if (isActive !== undefined) {
          whereConditions.isActive = isActive;
        }

        if (Object.keys(whereConditions).length > 0) {
          paginationOptions.where = whereConditions;
        }

        // Execute pagination query with extension
        const [studios, meta] = await ctx.db.studio
          .paginate(paginationOptions)
          .withPages({
            page,
          });

        return {
          studios,
          meta,
        };
      } catch (error) {
        throw new TRPCError({
          code: "INTERNAL_SERVER_ERROR",
          message: "Failed to list studios",
          cause: error,
        });
      }
    }),

  // Get studios managed by the current user
  myStudios: authedProcedure
    .input(
      z
        .object({
          role: z.enum(["OWNER", "ADMIN", "INSTRUCTOR", "STAFF"]).optional(),
        })
        .optional(),
    )
    .query(async ({ ctx, input }) => {
      try {
        const whereConditions: Prisma.StudioMemberWhereInput = {
          profileId: ctx.profile.id,
        };

        if (input?.role) {
          whereConditions.role = input.role;
        }

        const memberships = await ctx.db.studioMember.findMany({
          where: whereConditions,
          include: {
            studio: true,
          },
          orderBy: {
            createdAt: "desc",
          },
        });

        return memberships;
      } catch (error) {
        throw new TRPCError({
          code: "INTERNAL_SERVER_ERROR",
          message: "Failed to retrieve your studios",
          cause: error,
        });
      }
    }),

  // Add a member to a studio - using adminProcedure which checks for OWNER or ADMIN role
  addMember: adminProcedure
    .input(
      z.object({
        studioId: z.string().uuid(), // Already required by adminProcedure
        profileId: z.string().uuid(),
        role: z.enum(["ADMIN", "INSTRUCTOR", "STAFF"]).default("STAFF"),
      }),
    )
    .mutation(async ({ ctx, input }) => {
      try {
        // Check if the member already exists
        const existingMember = await ctx.db.studioMember.findUnique({
          where: {
            profileId_studioId: {
              profileId: input.profileId,
              studioId: input.studioId,
            },
          },
        });

        if (existingMember) {
          throw new TRPCError({
            code: "CONFLICT",
            message: "This person is already a member of this studio",
          });
        }

        // Create the member
        const member = await ctx.db.studioMember.create({
          data: {
            profileId: input.profileId,
            studioId: input.studioId,
            role: input.role,
          },
          include: {
            profile: true,
            studio: true,
          },
        });

        return member;
      } catch (error) {
        throw new TRPCError({
          code: "INTERNAL_SERVER_ERROR",
          message: "Failed to add member to studio",
          cause: error,
        });
      }
    }),

  // Custom procedure for updating members - we need specific logic for role restrictions
  updateMember: adminProcedure
    .input(
      z.object({
        studioId: z.string().uuid(), // Required by adminProcedure
        memberId: z.string().uuid(),
        role: z.enum(["ADMIN", "INSTRUCTOR", "STAFF"]).optional(),
      }),
    )
    .mutation(async ({ ctx, input }) => {
      // Get the member to update
      const memberToUpdate = await ctx.db.studioMember.findUnique({
        where: { id: input.memberId },
      });

      if (!memberToUpdate) {
        throw new TRPCError({
          code: "NOT_FOUND",
          message: "Member not found",
        });
