async function StudioProfile({ slug }: StudioProfileProps) {
  const studio = await api.studio.findUnique({ slug });

  if (!studio) {
    notFound();
  }

  // Format opening hours if they exist
  const formattedHours = studio.openingHours
    ? formatOpeningHours(studio.openingHours)
    : null;

  const cards = data.map((card, index) => (
    <CarouselCard key={card.src} card={card} index={index} />
  ));

  return (
    <div className="mx-auto max-w-7xl px-4 pb-20 pt-12 md:px-8">
      {/* Studio Header */}
      <div className="mb-12">
        <div className="flex flex-col items-start gap-6 md:flex-row md:items-center md:justify-between">
          <div className="flex items-center gap-5">
            <div className="relative h-20 w-20 overflow-hidden rounded-xl border border-neutral-200 bg-white shadow-sm dark:border-neutral-800 dark:bg-neutral-900 sm:h-24 sm:w-24">
              {studio.logo ? (
                <Image
                  src={studio.logo}
                  alt={`${studio.name} logo`}
                  fill
                  className="object-cover"
                />
              ) : (
                <div className="flex h-full w-full items-center justify-center bg-neutral-100 dark:bg-neutral-800">
                  <span className="text-2xl font-bold text-neutral-500 dark:text-neutral-400">
                    {studio.name.charAt(0)}
                  </span>
                </div>
              )}
            </div>
            <div>
              <div className="flex items-center gap-3">
                <h1 className="text-3xl font-bold text-neutral-900 dark:text-white">
                  {studio.name}
                </h1>
                {studio.isActive && (
                  <Badge
                    variant="outline"
                    className="border-green-500 text-green-600 dark:text-green-400"
                  >
                    Active
                  </Badge>
                )}
              </div>
              <p className="mt-1 text-neutral-600 dark:text-neutral-400">
                {studio.description || "Welcome to our dance studio!"}
              </p>
            </div>
          </div>
        </div>
      </div>

      <Carousel items={cards} />
      <AnimatedTestimonialsWrapper testimonials={testimonials} />

      {/* Bento Grid Layout - Rearranged order */}
      <div className="grid grid-cols-1 gap-6 md:grid-cols-3">
        {/* Studio Atmosphere Card - Now first */}
        <Card className="col-span-1 h-auto border border-neutral-200 p-6 shadow-sm transition-all hover:shadow-md dark:border-neutral-800">
          <div className="mb-4 flex items-center gap-2">
            <Music className="h-5 w-5 text-indigo-600 dark:text-indigo-400" />
            <h3 className="text-lg font-semibold text-neutral-900 dark:text-white">
              Studio Atmosphere
            </h3>
          </div>
          <p className="line-clamp-3 text-neutral-700 dark:text-neutral-300">
            Experience a welcoming environment with state-of-the-art facilities,
            professional sound systems, and spacious dance floors.
          </p>
          <div className="mt-4 grid grid-cols-3 gap-2">
            {[...Array(3)].map((_, i) => (
              <div
                key={i}
                className="aspect-square overflow-hidden rounded-lg bg-neutral-100 dark:bg-neutral-800"
              >
                <div className="flex h-full w-full items-center justify-center text-neutral-400 dark:text-neutral-600">
                  <span className="text-xs">Image</span>
                </div>
              </div>
            ))}
          </div>
        </Card>

        {/* Location Card - Now second */}
        <Card className="col-span-1 flex h-auto flex-col justify-between border border-neutral-200 p-6 shadow-sm transition-all hover:shadow-md dark:border-neutral-800">
          <div>
            <div className="mb-4 flex items-center gap-2">
              <MapPin className="h-5 w-5 text-indigo-600 dark:text-indigo-400" />
              <h3 className="text-lg font-semibold text-neutral-900 dark:text-white">
                Location
              </h3>
            </div>
            <div className="text-neutral-700 dark:text-neutral-300">
              <p className="font-medium">{formatAddress(studio)}</p>
              {studio.countryName && (
                <p className="mt-1 text-sm text-neutral-500 dark:text-neutral-400">
                  {studio.stateName || ""} {studio.countryName}
                </p>
              )}
            </div>
          </div>
          <a
            href={`https://maps.google.com/?q=${formatAddress(studio)}`}
            target="_blank"
            rel="noopener noreferrer"
            className="mt-4 inline-block text-sm font-medium text-indigo-600 hover:text-indigo-700 hover:underline dark:text-indigo-400 dark:hover:text-indigo-300"
          >
            View on map →
          </a>
        </Card>

        {/* Professional Instructors Card - Now third */}
        <Card className="col-span-1 flex h-auto flex-col border border-neutral-200 p-6 shadow-sm transition-all hover:shadow-md dark:border-neutral-800">
          <div className="mb-4 flex items-center gap-2">
            <Users className="h-5 w-5 text-indigo-600 dark:text-indigo-400" />
            <h3 className="text-lg font-semibold text-neutral-900 dark:text-white">
              Professional Instructors
            </h3>
          </div>
          <p className="text-neutral-700 dark:text-neutral-300">
            Learn from our team of experienced and passionate dance instructors,
            each specialized in their own dance styles.
          </p>
          <div className="mt-4 flex -space-x-3">
            {[...Array(4)].map((_, i) => (
              <div
                key={i}
                className="relative h-10 w-10 overflow-hidden rounded-full border-2 border-white bg-neutral-100 dark:border-neutral-900 dark:bg-neutral-800"
              >
                <div className="flex h-full w-full items-center justify-center text-sm font-medium text-neutral-500 dark:text-neutral-400">
                  {String.fromCharCode(65 + i)}
                </div>
              </div>
            ))}
            <div className="relative flex h-10 w-10 items-center justify-center rounded-full border-2 border-white bg-indigo-100 text-sm font-medium text-indigo-600 dark:border-neutral-900 dark:bg-indigo-900 dark:text-indigo-300">
              +3
            </div>
          </div>
        </Card>

        {/* Opening Hours Card - Now last */}
        <Card className="col-span-1 flex h-auto flex-col border border-neutral-200 p-6 shadow-sm transition-all hover:shadow-md dark:border-neutral-800">
          <div>
            <div className="mb-4 flex items-center gap-2">
              <Clock className="h-5 w-5 text-indigo-600 dark:text-indigo-400" />
              <h3 className="text-lg font-semibold text-neutral-900 dark:text-white">
                Opening Hours
              </h3>
            </div>
            {formattedHours ? (
              <div className="text-neutral-700 dark:text-neutral-300">
                <div className="max-h-[200px] overflow-y-auto pr-2">
                  {formattedHours.map((day) => (
                    <div key={day.day} className="flex justify-between py-1">
                      <span className="font-medium">{day.day}</span>
                      <span className="text-right">{day.hours}</span>
                    </div>
                  ))}
                </div>
              </div>
            ) : (
              <p className="text-neutral-600 dark:text-neutral-400">
                Hours not available
              </p>
            )}
          </div>
        </Card>
      </div>

      {/* Option 2: Full-width button at the bottom */}
      <div className="mt-10">
        <Link href={`/studio/${slug}/schedules`}>
          <Button className="group relative w-full overflow-hidden bg-indigo-600 py-7 text-lg font-medium text-white transition-all hover:bg-indigo-700 dark:bg-indigo-700 dark:hover:bg-indigo-600">
            <span className="flex items-center justify-center gap-3">
              <Calendar className="h-5 w-5" />
              <span>Discover Dance Classes</span>
              <ArrowRight className="h-5 w-5 transition-transform duration-300 group-hover:translate-x-2" />
            </span>
          </Button>
        </Link>
      </div>
    </div>
  );
}

type StudioPageProps = {
  params: {
    slug: string;
  };
};

export default async function StudioPage({ params }: StudioPageProps) {
  const { slug } = params;

  // Prefetch the data to improve performance
  await api.studio.findUnique.prefetch({ slug });

  return (
    <main className="min-h-screen bg-white dark:bg-neutral-950">
      <Suspense fallback={<StudioProfileSkeleton />}>
        <StudioProfile slug={slug} />
      </Suspense>
    </main>
  );
}

function StudioProfileSkeleton() {
  return (
    <div className="mx-auto max-w-7xl px-4 pb-20 pt-12 md:px-8">
