    return await db.transaction(async (trx) => {
      const shop = await ShopsService.withTransaction(trx).create(data);

      if (data.addresses) {
        const addresses = await Promise.all(
          data.addresses.map((item) => {
            const address = AddressModule.withTransaction(trx).create({
              city: item.address.city,
              country: item.address.country,
              zip: item.address.zip,
            });

            const shopAddress = ShopsAddressesService.withTransaction(trx)
              .create({
                shopId: shop.id,
                addressId: address.id,
                street: item.street,
                type: item.type,
                isMain: item.isMain,
                unit: item.unit,
              });

            return shopAddress;
          }),
        );
      }
    });
