import { Transaction } from "@/types/drizzle.type.ts";
import { db } from "@/db/index.ts";
import { BaseService } from "@/lib/base-service.ts";
import { productImages } from "@/db/schema/index.ts";
import {
  ProductImageInsert,
  ProductImageUpdate,
} from "@/features/products/products.types.ts";
import { and, eq } from "drizzle-orm";

type FindFirstWhere = {
  id?: string;
  productId?: string;
  variantId?: string;
  isMain?: boolean;
};

type FindManyWhere = {
  productId?: string;
  variantId?: string;
};

export class ProductsImagesService extends BaseService {
  constructor(trx: Transaction = db) {
    super(trx);
  }

  async create(input: ProductImageInsert) {
    const [productImage] = await this.trx.insert(productImages).values(input)
      .returning();
    return productImage;
  }

  async update(id: string, input: ProductImageUpdate) {
    const [productImage] = await this.trx.update(productImages).set(input)
      .where(eq(productImages.id, id))
      .returning();
    return productImage;
  }

  async delete(id: string) {
    const [productImage] = await this.trx.delete(productImages)
      .where(eq(productImages.id, id))
      .returning();
    return productImage;
  }

  async findMany(where: FindManyWhere) {
    const conditions = [
      where.productId
        ? eq(productImages.productId, where.productId)
        : undefined,
      where.variantId
        ? eq(productImages.variantId, where.variantId)
        : undefined,
    ].filter(Boolean);

    const whereConditions = conditions.length > 0
      ? and(...conditions)
      : undefined;

    return await this.trx.query.productImages.findMany({
      where: whereConditions,
    });
  }

  async findFirst(where: FindFirstWhere) {
    const conditions = [
      where.id ? eq(productImages.id, where.id) : undefined,
      where.productId
        ? eq(productImages.productId, where.productId)
        : undefined,
      where.variantId
        ? eq(productImages.variantId, where.variantId)
        : undefined,
      where.isMain ? eq(productImages.isMain, where.isMain) : undefined,
    ].filter(Boolean);

    const whereConditions = conditions.length > 0
      ? and(...conditions)
      : undefined;

    return await this.trx.query.productImages.findFirst({
      where: whereConditions,
    });
  }
}
