import { Transaction } from "@/types/drizzle.type.ts";
import { db } from "@/db/index.ts";
import { BaseService } from "@/lib/base-service.ts";
import { productCategories, productToCategories } from "@/db/schema/index.ts";
import { and, eq, isNull } from "drizzle-orm";
import { InferInsertModel, InferSelectModel } from "drizzle-orm";

type ProductCategoryInsert = InferInsertModel<typeof productCategories>;
type ProductCategoryUpdate = Partial<ProductCategoryInsert>;
type ProductToCategoryInsert = InferInsertModel<typeof productToCategories>;

type FindFirstWhere = {
  id?: string;
  slug?: string;
  parentId?: string | null;
};

type FindManyWhere = {
  parentId?: string | null;
};

export class ProductsCategoriesService extends BaseService {
  constructor(trx: Transaction = db) {
    super(trx);
  }

  async create(input: ProductCategoryInsert) {
    const [category] = await this.trx.insert(productCategories).values(input)
      .returning();
    return category;
  }

  async update(id: string, input: ProductCategoryUpdate) {
    const [category] = await this.trx.update(productCategories).set(input)
      .where(eq(productCategories.id, id))
      .returning();
    return category;
  }

  async delete(id: string) {
    const [category] = await this.trx.delete(productCategories)
      .where(eq(productCategories.id, id))
      .returning();
    return category;
  }

  async findMany(where: FindManyWhere = {}) {
    let conditions = [];

    if (where.parentId === null) {
      conditions.push(isNull(productCategories.parentId));
    } else if (where.parentId !== undefined) {
      conditions.push(eq(productCategories.parentId, where.parentId));
    }

    const whereConditions = conditions.length > 0
      ? and(...conditions)
      : undefined;

    return await this.trx.query.productCategories.findMany({
      where: whereConditions,
      with: {
        children: true
      }
    });
  }

  async findFirst(where: FindFirstWhere) {
    const conditions = [
      where.id ? eq(productCategories.id, where.id) : undefined,
      where.slug ? eq(productCategories.slug, where.slug) : undefined,
      where.parentId === null 
        ? isNull(productCategories.parentId) 
        : where.parentId 
          ? eq(productCategories.parentId, where.parentId) 
          : undefined,
    ].filter(Boolean);

    const whereConditions = conditions.length > 0
      ? and(...conditions)
      : undefined;

    return await this.trx.query.productCategories.findFirst({
      where: whereConditions,
      with: {
        children: true,
        parent: true
      }
    });
  }

  // Connect product to category
  async addProductToCategory(productId: string, categoryId: string) {
    const [relation] = await this.trx.insert(productToCategories)
      .values({ productId, categoryId })
      .returning();
    return relation;
  }

  // Remove product from category
  async removeProductFromCategory(productId: string, categoryId: string) {
    const [relation] = await this.trx.delete(productToCategories)
      .where(
        and(
          eq(productToCategories.productId, productId),
          eq(productToCategories.categoryId, categoryId)
        )
      )
      .returning();
    return relation;
  }

  // Get all categories for a product
  async getCategoriesByProductId(productId: string) {
    const relations = await this.trx.query.productToCategories.findMany({
      where: eq(productToCategories.productId, productId),
      with: {
        category: true
      }
    });

    return relations.map(rel => rel.category);
  }

  // Get all products in a category
  async getProductsByCategoryId(categoryId: string) {
    const relations = await this.trx.query.productToCategories.findMany({
      where: eq(productToCategories.categoryId, categoryId),
      with: {
        product: true
      }
    });

    return relations.map(rel => rel.product);
  }
}
